// src/App.js
import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { FiUpload, FiDownload, FiPlay, FiSquare, FiSun, FiMoon, FiHelpCircle, FiUsers } from 'react-icons/fi';

const BASE_URL = "https://emis.dhis2nigeria.org.ng/dhis/api";
const HEADERS = {
  'Content-Type': 'application/json',
  'Authorization': 'ApiToken d2pat_DAULytLK7GUBSlfknZ6ffVxIfifC8T9c3885349470'
};

// Sample user data for download
const SAMPLE_USERS = [
  {
    "username": "teacher1",
    "password": "Password123!",
    "firstName": "John",
    "surname": "Doe",
    "email": "teacher1@school.edu",
    "userRoles": [{"id": "abcdef"}],
    "userGroups": [{"id": "ghijk"}],
    "organisationUnits": [{"id": "lmnop"}]
  },
  {
    "username": "admin1",
    "password": "AdminPass123!",
    "firstName": "Jane",
    "surname": "Smith",
    "email": "admin@school.edu",
    "userRoles": [{"id": "qrstu"}],
    "userGroups": [{"id": "vwxyz"}],
    "organisationUnits": [{"id": "12345"}]
  }
];

function App() {
  const [users, setUsers] = useState([]);
  const [log, setLog] = useState([]);
  const [processing, setProcessing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [exportedUsers, setExportedUsers] = useState([]);
  const [usernameFilter, setUsernameFilter] = useState('');
  const [orgUnitFilter, setOrgUnitFilter] = useState('');
  const [theme, setTheme] = useState('dark');
  const [currentPage, setCurrentPage] = useState(1);
  const [activeTab, setActiveTab] = useState('import');
  const [batchSize, setBatchSize] = useState(50);
  const [selectedColumns, setSelectedColumns] = useState([
    'id', 'name', 'username', 'userGroups', 'userRoles', 'lastLogin', 'schoolPath'
  ]);
  const shouldStop = useRef(false);
  const logEndRef = useRef(null);

  const usersPerPage = 100;
  const availableColumns = [
    { id: 'id', label: 'ID' },
    { id: 'name', label: 'Name' },
    { id: 'username', label: 'Username' },
    { id: 'userGroups', label: 'Groups' },
    { id: 'userRoles', label: 'Roles' },
    { id: 'lastLogin', label: 'Last Login' },
    { id: 'schoolPath', label: 'School Path' },
    { id: 'schoolUID', label: 'School UID' }
  ];

  // Auto-scroll log to bottom
  useEffect(() => {
    logEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [log]);

  const appendLog = (message, type = 'info') => {
    const timestamp = new Date().toLocaleTimeString();
    setLog(prevLog => [...prevLog, { message, type, timestamp }]);
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();

    reader.onloadstart = () => {
      appendLog('⏳ Loading user file...', 'info');
    };

    reader.onload = event => {
      try {
        const data = JSON.parse(event.target.result);
        if (!Array.isArray(data)) {
          throw new Error('File should contain an array of users');
        }
        setUsers(data);
        appendLog(`✅ Loaded ${data.length} users`, 'success');
      } catch (error) {
        appendLog(`❌ Error parsing file: ${error.message}`, 'error');
      }
    };

    reader.onerror = () => {
      appendLog('❌ Failed to read file', 'error');
    };

    reader.readAsText(file);
  };

  const downloadSampleJson = () => {
    try {
      const blob = new Blob([JSON.stringify(SAMPLE_USERS, null, 2)], { type: 'application/json' });
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.download = 'dhis2_users_sample.json';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      appendLog('✅ Downloaded sample JSON file', 'success');
    } catch (error) {
      appendLog(`❌ Error downloading sample: ${error.message}`, 'error');
    }
  };

  const getUserId = async (username) => {
    try {
      const res = await axios.get(`${BASE_URL}/users?filter=username:eq:${username}&fields=id`, { headers: HEADERS });
      return res.data.users?.[0]?.id || null;
    } catch (error) {
      appendLog(`⚠️ Error fetching user ID for ${username}: ${error.message}`, 'warning');
      return null;
    }
  };

  const updateUser = async (user, id) => {
    try {
      const copy = { ...user };
      delete copy.password;
      await axios.put(`${BASE_URL}/users/${id}`, copy, { headers: HEADERS });
      appendLog(`🔄 Updated user: ${user.username}`, 'success');
      return true;
    } catch (error) {
      appendLog(`❌ Error updating user ${user.username}: ${error.message}`, 'error');
      return false;
    }
  };

  const createUser = async (user) => {
    try {
      await axios.post(`${BASE_URL}/users`, user, { headers: HEADERS });
      appendLog(`🆕 Created user: ${user.username}`, 'success');
      return true;
    } catch (error) {
      if (error.response?.status === 409) {
        const id = await getUserId(user.username);
        if (id) {
          appendLog(`⚠️ Conflict: Updating user ${user.username}`, 'warning');
          return await updateUser(user, id);
        } else {
          appendLog(`❌ Conflict and ID not found: ${user.username}`, 'error');
          return false;
        }
      } else {
        appendLog(`❌ Error for ${user.username}: ${error.message}`, 'error');
        return false;
      }
    }
  };

  const processUsers = async () => {
    setProcessing(true);
    shouldStop.current = false;
    setProgress(0);
    setLog([]);
    appendLog('🚀 Starting user import process...', 'info');

    let successCount = 0;
    let errorCount = 0;

    // Process in batches
    for (let i = 0; i < users.length; i += batchSize) {
      if (shouldStop.current) {
        appendLog("🛑 Process stopped by user.", 'warning');
        break;
      }

      const batch = users.slice(i, i + batchSize);
      appendLog(`🔨 Processing batch ${i / batchSize + 1} of ${Math.ceil(users.length / batchSize)}`, 'info');

      const batchResults = await Promise.all(
        batch.map(async (user) => {
          try {
            const id = await getUserId(user.username);
            const result = id ? await updateUser(user, id) : await createUser(user);
            return result;
          } catch (error) {
            appendLog(`❌ Batch error for ${user.username}: ${error.message}`, 'error');
            return false;
          }
        })
      );

      successCount += batchResults.filter(r => r).length;
      errorCount += batchResults.filter(r => !r).length;
      setProgress(Math.round(((i + batchSize) / users.length) * 100));
    }

    appendLog(`🎉 Process completed. Success: ${successCount}, Errors: ${errorCount}`, 
      errorCount === 0 ? 'success' : errorCount > successCount ? 'error' : 'warning');
    setProcessing(false);
  };

  const stopProcessing = () => {
    shouldStop.current = true;
    appendLog("⏹ Stopping process after current batch completes...", 'warning');
  };

  const exportUsersToCSV = async () => {
    appendLog("📦 Starting DHIS2 user export...", 'info');
    setProcessing(true);

    try {
      let seenIds = new Set();
      let usersList = [];
      let nextUrl = `${BASE_URL}/users.json?fields=id,name,username,userGroups[name],userRoles[name],lastLogin,organisationUnits[ancestors[name],name,id]&paging=true&pageSize=1000`;

      while (nextUrl) {
        const response = await axios.get(nextUrl, { headers: HEADERS });
        if (response.status !== 200) break;

        const data = response.data;
        const users = data.users || [];
        const pager = data.pager || {};

        for (const user of users) {
          const uid = user.id;
          if (!uid || seenIds.has(uid)) continue;
          seenIds.add(uid);

          const userGroups = user.userGroups?.map(g => g.name).join("; ") || "";
          const userRoles = user.userRoles?.map(r => r.name).join("; ") || "";
          const lastLogin = user.lastLogin || "";

          let schoolPaths = [];
          let schoolUIDs = [];

          (user.organisationUnits || []).forEach(ou => {
            if (ou.ancestors?.length === 4) {
              const path = [...ou.ancestors.map(a => a.name), ou.name].join(" > ");
              schoolPaths.push(path);
              schoolUIDs.push(ou.id || "");
            }
          });

          usersList.push({
            id: uid,
            name: user.name || '',
            username: user.username || '',
            userGroups,
            userRoles,
            lastLogin,
            schoolPath: schoolPaths.join(" | "),
            schoolUID: schoolUIDs.join(" | ")
          });
        }

        appendLog(`✅ Page ${pager.page} fetched. Total: ${seenIds.size}`, 'success');
        nextUrl = pager.nextPage || null;
      }

      setExportedUsers(usersList);
      appendLog(`🎉 Export complete. Total users: ${seenIds.size}`, 'success');
    } catch (error) {
      appendLog(`❌ Export failed: ${error.message}`, 'error');
    } finally {
      setProcessing(false);
    }
  };

  const exportFilteredUsers = (format = 'csv') => {
    const filtered = exportedUsers.filter(u =>
      u.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
      u.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
    );

    if (filtered.length === 0) {
      appendLog('⚠️ No users match your filters', 'warning');
      return;
    }

    try {
      let content, mimeType, extension;

      if (format === 'csv') {
        const headers = availableColumns
          .filter(col => selectedColumns.includes(col.id))
          .map(col => col.label);

        const rows = filtered.map(user => 
          selectedColumns.map(col => {
            const value = user[col] || '';
            return value.includes(',') ? `"${value}"` : value;
          }).join(',')
        );

        content = [headers.join(','), ...rows].join('\n');
        mimeType = 'text/csv;charset=utf-8;';
        extension = 'csv';
      } else {
        content = JSON.stringify(filtered, null, 2);
        mimeType = 'application/json';
        extension = 'json';
      }

      const blob = new Blob([content], { type: mimeType });
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.setAttribute('download', `dhis2_users_${new Date().toISOString().slice(0,10)}.${extension}`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      appendLog(`✅ Exported ${filtered.length} users as ${format.toUpperCase()}`, 'success');
    } catch (error) {
      appendLog(`❌ Export error: ${error.message}`, 'error');
    }
  };

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  const filteredUsers = exportedUsers.filter(user =>
    user.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
    user.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
  );

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);

  const handleNextPage = () => {
    if (currentPage < totalPages) setCurrentPage(currentPage + 1);
  };

  const handlePrevPage = () => {
    if (currentPage > 1) setCurrentPage(currentPage - 1);
  };

  const toggleColumn = (columnId) => {
    setSelectedColumns(prev => 
      prev.includes(columnId)
        ? prev.filter(id => id !== columnId)
        : [...prev, columnId]
    );
  };

  const getLogColor = (type) => {
    switch (type) {
      case 'error': return 'text-red-400';
      case 'warning': return 'text-yellow-400';
      case 'success': return 'text-green-400';
      default: return 'text-gray-300';
    }
  };

  return (
  <div className={`min-h-screen p-4 ${theme === 'dark' ? 'dark bg-gray-900' : 'bg-gray-50'}`}>
    <div className="max-w-7xl mx-auto rounded-2xl shadow-lg overflow-hidden">
      {/* Header */}
      <div className={`p-6 ${theme === 'dark' ? 'bg-gray-800' : 'bg-blue-600'} text-white`}>
        <div className="flex justify-between items-center">
          <div className="flex items-center space-x-3">
            <FiUsers className="text-2xl" />
            <h1 className="text-2xl font-bold">DHIS2 User Manager</h1>
          </div>
          <div className="flex space-x-4">
            <button 
              onClick={toggleTheme} 
              className="flex items-center space-x-1 bg-black bg-opacity-20 px-3 py-1 rounded"
            >
              {theme === 'light' ? <FiMoon /> : <FiSun />}
              <span>{theme === 'light' ? 'Dark' : 'Light'} Mode</span>
            </button>
            <button className="flex items-center space-x-1 bg-black bg-opacity-20 px-3 py-1 rounded">
              <FiHelpCircle />
              <span>Help</span>
            </button>
          </div>
        </div>
      </div>

        {/* Main Content */}
        <div className={`p-6 ${theme === 'dark' ? 'bg-gray-700' : 'bg-white'}`}>
          {/* Tabs */}
          <div className="flex border-b mb-6">
            <button
              className={`px-4 py-2 font-medium ${activeTab === 'import' ? 
                theme === 'dark' ? 'text-blue-300 border-b-2 border-blue-300' : 'text-blue-600 border-b-2 border-blue-600' : 
                theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`}
              onClick={() => setActiveTab('import')}
            >
              <FiUpload className="inline mr-2" />
              Import Users
            </button>
            <button
              className={`px-4 py-2 font-medium ${activeTab === 'export' ? 
                theme === 'dark' ? 'text-blue-300 border-b-2 border-blue-300' : 'text-blue-600 border-b-2 border-blue-600' : 
                theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`}
              onClick={() => setActiveTab('export')}
            >
              <FiDownload className="inline mr-2" />
              Export Users
            </button>
          </div>

          {/* Import Tab */}
          {activeTab === 'import' && (
            <div className="space-y-6">
              <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                <h2 className="text-lg font-semibold mb-3">Import Users</h2>
                <div className="flex flex-wrap gap-4">
                  <div className="flex-1 min-w-[300px]">
                    <label className="block mb-2 font-medium">Upload JSON File</label>
                    <div className="flex items-center space-x-2">
                      <label className={`flex-1 border rounded-lg p-2 cursor-pointer ${theme === 'dark' ? 'border-gray-600 bg-gray-900 hover:bg-gray-800' : 'border-gray-300 bg-white hover:bg-gray-50'}`}>
                        <FiUpload className="inline mr-2" />
                        Choose File
                        <input 
                          type="file" 
                          accept=".json" 
                          onChange={handleFileUpload} 
                          disabled={processing}
                          className="hidden" 
                        />
                      </label>
                      <button 
                        onClick={downloadSampleJson}
                        className={`px-4 py-2 rounded-lg flex items-center ${theme === 'dark' ? 'bg-purple-700 hover:bg-purple-600' : 'bg-purple-600 hover:bg-purple-500'} text-white`}
                      >
                        <FiDownload className="mr-2" />
                        Sample
                      </button>
                    </div>
                    {users.length > 0 && (
                      <p className="mt-2 text-sm text-green-500">
                        ✅ {users.length} users loaded
                      </p>
                    )}
                  </div>

                  <div className="flex-1 min-w-[300px]">
                    <label className="block mb-2 font-medium">Batch Settings</label>
                    <div className="flex items-center space-x-2">
                      <select
                        value={batchSize}
                        onChange={(e) => setBatchSize(Number(e.target.value))}
                        className={`border rounded-lg p-2 ${theme === 'dark' ? 'bg-gray-900 border-gray-600' : 'bg-white border-gray-300'}`}
                      >
                        <option value="1">1 users/batch</option>
                        <option value="2">2 users/batch</option>
                        <option value="3">3 users/batch</option>
                        <option value="4">4 users/batch</option>
                      </select>
                    </div>
                  </div>
                </div>
              </div>

              <div className="flex flex-wrap gap-4">
                <button 
                  onClick={processUsers} 
                  disabled={processing || users.length === 0}
                  className={`px-4 py-2 rounded-lg flex items-center ${processing || users.length === 0 ? 'bg-gray-400' : 'bg-green-600 hover:bg-green-500'} text-white`}
                >
                  <FiPlay className="mr-2" />
                  Start Import
                </button>
                <button 
                  onClick={stopProcessing} 
                  disabled={!processing}
                  className={`px-4 py-2 rounded-lg flex items-center ${!processing ? 'bg-gray-400' : 'bg-red-600 hover:bg-red-500'} text-white`}
                >
                  <FiSquare className="mr-2" />
                  Stop
                </button>
              </div>

              {processing && (
                <div className="w-full bg-gray-200 rounded-full h-2.5 overflow-hidden">
                  <div 
                    className="bg-blue-600 h-2.5 transition-all duration-300" 
                    style={{ width: `${progress}%` }} 
                  />
                </div>
              )}

              <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                <h3 className="font-medium mb-2">Activity Log</h3>
                <div className={`h-60 overflow-y-auto p-3 rounded ${theme === 'dark' ? 'bg-black' : 'bg-gray-200'}`}>
                  {log.length === 0 ? (
                    <p className={`text-center ${theme === 'dark' ? 'text-gray-500' : 'text-gray-600'}`}>
                      No activity yet
                    </p>
                  ) : (
                    <div className="font-mono text-sm space-y-1">
                      {log.map((entry, i) => (
                        <div key={i} className={getLogColor(entry.type)}>
                          <span className="opacity-70 mr-2">[{entry.timestamp}]</span>
                          {entry.message}
                        </div>
                      ))}
                      <div ref={logEndRef} />
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Export Tab */}
          {activeTab === 'export' && (
            <div className="space-y-6">
              <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                <h2 className="text-lg font-semibold mb-3">Export Users</h2>
                <div className="flex flex-wrap gap-4">
                  <button 
                    onClick={exportUsersToCSV} 
                    disabled={processing}
                    className={`px-4 py-2 rounded-lg flex items-center ${processing ? 'bg-gray-400' : 'bg-blue-600 hover:bg-blue-500'} text-white`}
                  >
                    <FiDownload className="mr-2" />
                    Fetch Users from DHIS2
                  </button>
                </div>
              </div>

              {exportedUsers.length > 0 && (
                <>
                  <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                    <h3 className="font-medium mb-3">Filter & Export</h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                      <div>
                        <label className="block mb-1">Username</label>
                        <input
                          type="text"
                          placeholder="Filter by username"
                          value={usernameFilter}
                          onChange={(e) => setUsernameFilter(e.target.value)}
                          className={`w-full p-2 rounded border ${theme === 'dark' ? 'bg-gray-900 border-gray-600' : 'bg-white border-gray-300'}`}
                        />
                      </div>
                      <div>
                        <label className="block mb-1">Organization Unit</label>
                        <input
                          type="text"
                          placeholder="Filter by org unit path"
                          value={orgUnitFilter}
                          onChange={(e) => setOrgUnitFilter(e.target.value)}
                          className={`w-full p-2 rounded border ${theme === 'dark' ? 'bg-gray-900 border-gray-600' : 'bg-white border-gray-300'}`}
                        />
                      </div>
                    </div>

                    <div className="mb-4">
                      <label className="block mb-2">Columns to Export</label>
                      <div className="flex flex-wrap gap-2">
                        {availableColumns.map(col => (
                          <label key={col.id} className="flex items-center space-x-1">
                            <input
                              type="checkbox"
                              checked={selectedColumns.includes(col.id)}
                              onChange={() => toggleColumn(col.id)}
                              className="rounded"
                            />
                            <span>{col.label}</span>
                          </label>
                        ))}
                      </div>
                    </div>

                    <div className="flex flex-wrap gap-3">
                      <button 
                        onClick={() => exportFilteredUsers('csv')}
                        className={`px-4 py-2 rounded-lg flex items-center ${theme === 'dark' ? 'bg-green-700 hover:bg-green-600' : 'bg-green-600 hover:bg-green-500'} text-white`}
                      >
                        <FiDownload className="mr-2" />
                        Export as CSV
                      </button>
                      <button 
                        onClick={() => exportFilteredUsers('json')}
                        className={`px-4 py-2 rounded-lg flex items-center ${theme === 'dark' ? 'bg-yellow-700 hover:bg-yellow-600' : 'bg-yellow-600 hover:bg-yellow-500'} text-white`}
                      >
                        <FiDownload className="mr-2" />
                        Export as JSON
                      </button>
                    </div>
                  </div>

                  <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                    <h3 className="font-medium mb-3">User Data ({filteredUsers.length} users)</h3>
                    <div className="overflow-x-auto">
                      <table className="min-w-full border-collapse">
                        <thead>
                          <tr className={`${theme === 'dark' ? 'bg-gray-900' : 'bg-gray-200'}`}>
                            {selectedColumns.includes('id') && <th className="p-2 border text-left">ID</th>}
                            {selectedColumns.includes('name') && <th className="p-2 border text-left">Name</th>}
                            {selectedColumns.includes('username') && <th className="p-2 border text-left">Username</th>}
                            {selectedColumns.includes('userGroups') && <th className="p-2 border text-left">Groups</th>}
                            {selectedColumns.includes('userRoles') && <th className="p-2 border text-left">Roles</th>}
                            {selectedColumns.includes('lastLogin') && <th className="p-2 border text-left">Last Login</th>}
                            {selectedColumns.includes('schoolPath') && <th className="p-2 border text-left">School Path</th>}
                            {selectedColumns.includes('schoolUID') && <th className="p-2 border text-left">School UID</th>}
                          </tr>
                        </thead>
                        <tbody>
                          {currentUsers.map((user, idx) => (
                            <tr 
                              key={idx} 
                              className={`${idx % 2 === 0 ? (theme === 'dark' ? 'bg-gray-700' : 'bg-white') : (theme === 'dark' ? 'bg-gray-800' : 'bg-gray-50')}`}
                            >
                              {selectedColumns.includes('id') && <td className="p-2 border text-xs">{user.id}</td>}
                              {selectedColumns.includes('name') && <td className="p-2 border text-xs">{user.name}</td>}
                              {selectedColumns.includes('username') && <td className="p-2 border text-xs">{user.username}</td>}
                              {selectedColumns.includes('userGroups') && <td className="p-2 border text-xs">{user.userGroups}</td>}
                              {selectedColumns.includes('userRoles') && <td className="p-2 border text-xs">{user.userRoles}</td>}
                              {selectedColumns.includes('lastLogin') && <td className="p-2 border text-xs">{user.lastLogin}</td>}
                              {selectedColumns.includes('schoolPath') && <td className="p-2 border text-xs">{user.schoolPath}</td>}
                              {selectedColumns.includes('schoolUID') && <td className="p-2 border text-xs">{user.schoolUID}</td>}
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>

                    {totalPages > 1 && (
                      <div className="mt-4 flex justify-between items-center">
                        <button 
                          onClick={handlePrevPage} 
                          disabled={currentPage === 1}
                          className={`px-4 py-2 rounded ${currentPage === 1 ? 'opacity-50 cursor-not-allowed' : ''} ${theme === 'dark' ? 'bg-gray-600 hover:bg-gray-500' : 'bg-gray-200 hover:bg-gray-300'}`}
                        >
                          Previous
                        </button>
                        <span>
                          Page {currentPage} of {totalPages}
                        </span>
                        <button 
                          onClick={handleNextPage} 
                          disabled={currentPage === totalPages}
                          className={`px-4 py-2 rounded ${currentPage === totalPages ? 'opacity-50 cursor-not-allowed' : ''} ${theme === 'dark' ? 'bg-gray-600 hover:bg-gray-500' : 'bg-gray-200 hover:bg-gray-300'}`}
                        >
                          Next
                        </button>
                      </div>
                    )}
                  </div>
                </>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
