// src/App.js
import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const BASE_URL = "https://emis.dhis2nigeria.org.ng/dhis/api";
const HEADERS = {
  'Content-Type': 'application/json',
  'Authorization': 'ApiToken d2pat_DAULytLK7GUBSlfknZ6ffVxIfifC8T9c3885349470'
};

function App() {
  const [users, setUsers] = useState([]);
  const [processing, setProcessing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [exportedUsers, setExportedUsers] = useState([]);
  const [usernameFilter, setUsernameFilter] = useState('');
  const [orgUnitFilter, setOrgUnitFilter] = useState('');
  const [theme, setTheme] = useState('light');
  const [currentPage, setCurrentPage] = useState(1);
  const [loadingExport, setLoadingExport] = useState(true);
  const usersPerPage = 10;
  const shouldStop = useRef(false);

  useEffect(() => {
    document.documentElement.classList.toggle('dark', theme === 'dark');
  }, [theme]);

  useEffect(() => {
    exportUsers();
  }, []);

  const exportUsers = async () => {
    const seenIds = new Set();
    let usersList = [];

    try {
      const firstPage = await axios.get(`${BASE_URL}/users.json?fields=id,name,username,userGroups[name],userRoles[name],lastLogin,organisationUnits[ancestors[name],name,id]&paging=true&pageSize=1000`, { headers: HEADERS });
      const total = firstPage.data.pager?.total || 1;
      let page = 1;
      let fetched = 0;
      let nextPage = true;

      while (nextPage) {
        const pageUrl = `${BASE_URL}/users.json?fields=id,name,username,userGroups[name],userRoles[name],lastLogin,organisationUnits[ancestors[name],name,id]&paging=true&pageSize=1000&page=${page}`;
        const response = await axios.get(pageUrl, { headers: HEADERS });
        const data = response.data;
        const users = data.users || [];

        for (const user of users) {
          const uid = user.id;
          if (!uid || seenIds.has(uid)) continue;
          seenIds.add(uid);

          const userGroups = user.userGroups?.map(g => g.name).join("; ") || "";
          const userRoles = user.userRoles?.map(r => r.name).join("; ") || "";
          const lastLogin = user.lastLogin || "";

          let schoolPaths = [];
          let schoolUIDs = [];

          (user.organisationUnits || []).forEach(ou => {
            if (ou.ancestors?.length === 4) {
              const path = [...ou.ancestors.map(a => a.name), ou.name].join(" > ");
              schoolPaths.push(path);
              schoolUIDs.push(ou.id || "");
            }
          });

          usersList.push({
            id: uid,
            name: user.name || '',
            username: user.username || '',
            userGroups,
            userRoles,
            lastLogin,
            schoolPath: schoolPaths.join(" | "),
            schoolUID: schoolUIDs.join(" | ")
          });
        }

        fetched += users.length;
        setProgress(Math.round((fetched / total) * 100));

        nextPage = !!data.pager?.nextPage;
        page++;
      }
    } catch (error) {
      console.error("Error fetching users:", error);
    }

    setExportedUsers(usersList);
    setLoadingExport(false);
    setProgress(100);
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onload = event => {
      const data = JSON.parse(event.target.result);
      setUsers(data);
    };

    reader.readAsText(file);
  };

  const getUserId = async (username) => {
    try {
      const res = await axios.get(`${BASE_URL}/users?filter=username:eq:${username}&fields=id`, { headers: HEADERS });
      return res.data.users?.[0]?.id || null;
    } catch {
      return null;
    }
  };

  const updateUser = async (user, id) => {
    const copy = { ...user };
    delete copy.password;
    await axios.put(`${BASE_URL}/users/${id}`, copy, { headers: HEADERS });
  };

  const createUser = async (user) => {
    try {
      await axios.post(`${BASE_URL}/users`, user, { headers: HEADERS });
    } catch (error) {
      if (error.response?.status === 409) {
        const id = await getUserId(user.username);
        if (id) await updateUser(user, id);
      }
    }
  };

  const processUsers = async () => {
    setProcessing(true);
    shouldStop.current = false;
    setProgress(0);

    for (let i = 0; i < users.length; i++) {
      if (shouldStop.current) break;

      const user = users[i];
      const id = await getUserId(user.username);

      if (id) {
        await updateUser(user, id);
      } else {
        await createUser(user);
      }

      setProgress(Math.round(((i + 1) / users.length) * 100));
    }

    setProcessing(false);
  };

  const stopProcessing = () => {
    shouldStop.current = true;
  };

  const exportFilteredUsersToCSV = () => {
    const filtered = exportedUsers.filter(u =>
      u.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
      u.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
    );

    const headers = ["id", "name", "username", "userGroups", "userRoles", "lastLogin", "schoolPath", "schoolUID"];
    const rows = filtered.map(user => [
      user.id,
      `"${user.name}"`,
      user.username,
      `"${user.userGroups}"`,
      `"${user.userRoles}"`,
      user.lastLogin,
      `"${user.schoolPath}"`,
      `"${user.schoolUID}"`
    ].join(","));

    const csvContent = [headers.join(","), ...rows].join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.setAttribute("download", `filtered_users_${new Date().toISOString().slice(0,10)}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const exportAllUsersToCSV = () => {
    const headers = ["id", "name", "username", "userGroups", "userRoles", "lastLogin", "schoolPath", "schoolUID"];
    const rows = exportedUsers.map(user => [
      user.id,
      `"${user.name}"`,
      user.username,
      `"${user.userGroups}"`,
      `"${user.userRoles}"`,
      user.lastLogin,
      `"${user.schoolPath}"`,
      `"${user.schoolUID}"`
    ].join(","));

    const csvContent = [headers.join(","), ...rows].join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.setAttribute("download", `all_users_${new Date().toISOString().slice(0,10)}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  const filteredUsers = exportedUsers.filter(user =>
    user.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
    user.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
  );

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);

  const handleNextPage = () => {
    if (currentPage < totalPages) setCurrentPage(currentPage + 1);
  };

  const handlePrevPage = () => {
    if (currentPage > 1) setCurrentPage(currentPage - 1);
  };

  return (
    <div className={`${theme === 'dark' ? 'dark' : ''} min-h-screen p-6`}>  
      <div className="max-w-6xl mx-auto rounded-2xl shadow p-6 bg-gray-100 text-black dark:bg-gray-900 dark:text-white">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-2xl font-bold text-blue-700 dark:text-blue-300">DHIS2 User Manager</h1>
          <button onClick={toggleTheme} className="text-sm text-gray-600 dark:text-gray-300 hover:underline">
            Toggle {theme === 'light' ? 'Dark' : 'Light'} Mode
          </button>
        </div>

        {loadingExport ? (
          <div className="flex flex-col items-center justify-center h-96">
            <div className="h-12 w-12 border-4 border-blue-500 border-t-transparent border-solid rounded-full animate-spin"></div>
            <p className="mt-4 text-lg font-medium">Loading Users... {progress}%</p>
          </div>
        ) : (
          // The rest of your interface goes here...
          <div> {/* UI content */} </div>
        )}
      </div>
    </div>
  );
}

export default App;
