import React, { useState, useRef, useEffect, useCallback } from 'react';
import { init, useDataEngine, useAlertBar } from '@dhis2/app-runtime';
import { CircularLoader, Button, Input, Table, TableBody, TableCell, TableRow, TableHead, TableHeader, Layer } from '@dhis2/ui';
import axios from 'axios';
import { FiUpload, FiDownload, FiPlay, FiSquare, FiSun, FiMoon, FiHelpCircle, FiUsers } from 'react-icons/fi';
import { convertCSVtoJSON } from './convertCSVtoJSON';

// Configure axios defaults
axios.defaults.timeout = 30000; // 30 second timeout
axios.defaults.maxContentLength = 50 * 1024 * 1024; // 50MB
axios.defaults.maxBodyLength = 50 * 1024 * 1024; // 50MB

const BASE_URL = process.env.REACT_APP_BASE_URL || "https://emis.dhis2nigeria.org.ng/dhis/api";
const HEADERS = {
  'Content-Type': 'application/json',
  'Authorization': `ApiToken ${process.env.REACT_APP_API_TOKEN}`,
};

// Sample user data for download
const SAMPLE_USERS = [
  {
    firstName: "PVT",
    surname: "Royal",
    username: 12020407,
    password: "Egor123@",
    userRoles: [{ id: "KBkjSGFKSKI" }, { id: "oO6BBApzmHZ" }],
    organisationUnits: [{ id: "wRQIw3NMW29" }],
    dataViewOrganisationUnits: [{ id: "wRQIw3NMW29" }],
    teiSearchOrganisationUnits: [{ id: "wRQIw3NMW29" }],
    userGroups: [{ id: "U8WuGyxfFFp" }],
  },
  {
    firstName: "PVT",
    surname: "Dominion",
    username: 12020409,
    password: "Egor123@",
    userRoles: [{ id: "KBkjSGFKSKI" }, { id: "oO6BBApzmHZ" }],
    organisationUnits: [{ id: "AVEyJypVCjJ" }],
    dataViewOrganisationUnits: [{ id: "AVEyJypVCjJ" }],
    teiSearchOrganisationUnits: [{ id: "AVEyJypVCjJ" }],
    userGroups: [{ id: "U8WuGyxfFFp" }],
  },
];

const MyApp = () => {
  const [users, setUsers] = useState([]);
  const [log, setLog] = useState([]);
  const [processing, setProcessing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [exportedUsers, setExportedUsers] = useState([]);
  const [usernameFilter, setUsernameFilter] = useState('');
  const [orgUnitFilter, setOrgUnitFilter] = useState('');
  const [theme, setTheme] = useState('dark');
  const [currentPage, setCurrentPage] = useState(1);
  const [activeTab, setActiveTab] = useState('import');
  const [batchSize, setBatchSize] = useState(2);
  const [connectionStatus, setConnectionStatus] = useState('checking...');
  const [selectedColumns, setSelectedColumns] = useState([
    'id', 'name', 'username', 'userGroups', 'userRoles', 'lastLogin', 'schoolPath',
  ]);
  const [showHelp, setShowHelp] = useState(false);

  const shouldStop = useRef(false);
  const logEndRef = useRef(null);
  const engine = useDataEngine();
  const { add: addAlert } = useAlertBar();

  const usersPerPage = 100;
  const availableColumns = [
    { id: 'id', label: 'ID' },
    { id: 'name', label: 'Name' },
    { id: 'username', label: 'Username' },
    { id: 'userGroups', label: 'Groups' },
    { id: 'userRoles', label: 'Roles' },
    { id: 'lastLogin', label: 'Last Login' },
    { id: 'schoolPath', label: 'School Path' },
    { id: 'schoolUID', label: 'School UID' },
  ];

  const appendLog = (message, type = 'info') => {
    const timestamp = new Date().toLocaleTimeString();
    setLog(prevLog => {
      const newLog = [...prevLog, { message, type, timestamp }];
      return newLog.slice(-100);
    });
    if (type === 'error') addAlert({ message, critical: true });
  };

  const apiRequest = useCallback(async (config, attempt = 1) => {
    const maxRetries = 3;
    const baseDelay = 1000;

    try {
      const response = await axios({
        ...config,
        timeout: 30000,
        headers: { ...HEADERS, ...config.headers },
        baseURL: BASE_URL,
      });
      return response;
    } catch (error) {
      if (attempt >= maxRetries) throw error;
      const retryDelay = baseDelay * Math.pow(2, attempt - 1);
      appendLog(`⚠️ Attempt ${attempt} failed, retrying in ${retryDelay/1000}s...`, 'warning');
      await new Promise(resolve => setTimeout(resolve, retryDelay));
      return apiRequest(config, attempt + 1);
    }
  }, [appendLog]);

  useEffect(() => {
    let retries = 0;
    const maxRetries = 3;

    const checkConnection = async () => {
      try {
        await engine.query({ resource: 'system/ping' });
        setConnectionStatus('connected');
        retries = 0;
      } catch {
        retries++;
        if (retries >= maxRetries) {
          setConnectionStatus('disconnected');
          appendLog('⚠️ Failed to connect to server after retries', 'warning');
        } else {
          setTimeout(checkConnection, 1000 * Math.pow(2, retries));
        }
      }
    };

    checkConnection();
    const interval = setInterval(checkConnection, 30000);
    return () => clearInterval(interval);
  }, [engine]);

  useEffect(() => {
    logEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [log]);

  const handleFileUpload = (e) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadstart = () => appendLog('⏳ Loading user file...', 'info');
    reader.onload = event => {
      try {
        if (file.name.endsWith('.csv')) {
          const convertedUsers = convertCSVtoJSON(event.target.result);
          if (convertedUsers) {
            setUsers(convertedUsers);
            appendLog(`✅ Loaded and converted ${convertedUsers.length} users from CSV`, 'success');
          }
        } else {
          const data = JSON.parse(event.target.result);
          if (!Array.isArray(data)) throw new Error('File should contain an array of users');
          setUsers(data);
          appendLog(`✅ Loaded ${data.length} users from JSON`, 'success');
        }
      } catch (error) {
        appendLog(`❌ Error parsing file: ${error.message}`, 'error');
      }
    };
    reader.onerror = () => appendLog('❌ Failed to read file', 'error');
    reader.readAsText(file);
  };

  const downloadFile = (content, filename, mimeType) => {
    try {
      const blob = new Blob([content], { type: mimeType });
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.download = filename;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      appendLog(`✅ Downloaded ${filename}`, 'success');
    } catch (error) {
      appendLog(`❌ Download failed: ${error.message}`, 'error');
    }
  };

  const downloadSampleJson = () => {
    downloadFile(JSON.stringify(SAMPLE_USERS, null, 2), 'dhis2_users_sample.json', 'application/json');
  };

  const downloadSampleCSV = () => {
    const headers = [
      'firstName',
      'surname',
      'username',
      'password',
      'userRoles',
      'organisationUnits',
      'dataViewOrganisationUnits',
      'teiSearchOrganisationUnits',
      'userGroups',
    ].join(',');

    const sampleRow = [
      'JSS',
      'Com',
      '11010051',
      'Abakaliki123@',
      '[{"id": "KBkjSGFKSKI"},{"id": "oO6BBApzmHZ"}]',
      '[{"id": "wMcSLrAHchs"}]',
      '[{"id": "wMcSLrAHchs"}]',
      '[{"id": "wMcSLrAHchs"}]',
      '[{"id": "Ca67o1mgiPn"}]',
    ].map(field => `"${field}"`).join(',');

    const csvContent = [headers, sampleRow].join('\n');
    downloadFile(csvContent, 'dhis2_users_template.csv', 'text/csv;charset=utf-8;');
  };

  const validateUser = (user) => {
    if (!user.username) return 'Username is required';
    if (!user.userRoles?.length) return 'At least one user role is required';
    if (!user.organisationUnits?.length) return 'At least one organization unit is required';
    return null;
  };

  const getUserId = async (username) => {
    try {
      const res = await engine.query({
        resource: 'users',
        params: { filter: `username:eq:${username}`, fields: 'id' },
      });
      return res.users?.[0]?.id || null;
    } catch (error) {
      appendLog(`⚠️ Error fetching user ID for ${username}: ${error.message}`, 'warning');
      return null;
    }
  };

  const updateUser = async (user, id) => {
    try {
      const payload = {
        firstName: user.firstName,
        surname: user.surname,
        username: user.username,
        userRoles: user.userRoles.map(role => ({ id: role.id })),
        organisationUnits: user.organisationUnits.map(ou => ({ id: ou.id })),
        dataViewOrganisationUnits: user.dataViewOrganisationUnits.map(ou => ({ id: ou.id })),
        teiSearchOrganisationUnits: user.teiSearchOrganisationUnits.map(ou => ({ id: ou.id })),
        userGroups: user.userGroups.map(group => ({ id: group.id })),
      };

      await engine.mutate({
        resource: `users/${id}`,
        type: 'update',
        data: payload,
      });

      appendLog(`🔄 Updated user: ${user.username}`, 'success');
      return true;
    } catch (error) {
      appendLog(`❌ Error updating user ${user.username}: ${error.message}`, 'error');
      return false;
    }
  };

  const createUser = async (user) => {
    try {
      await engine.mutate({
        resource: 'users',
        type: 'create',
        data: user,
      });
      appendLog(`🆕 Created user: ${user.username}`, 'success');
      return true;
    } catch (error) {
      if (error.code === 409) {
        const id = await getUserId(user.username);
        if (id) {
          appendLog(`⚠️ Conflict: Updating user ${user.username}`, 'warning');
          return await updateUser(user, id);
        } else {
          appendLog(`❌ Conflict and ID not found: ${user.username}`, 'error');
          return false;
        }
      }
      appendLog(`❌ Error for ${user.username}: ${error.message}`, 'error');
      return false;
    }
  };

  const processBatch = async (batch) => {
    const promises = batch.map(async (user) => {
      try {
        const id = await getUserId(user.username);
        return id ? await updateUser(user, id) : await createUser(user);
      } catch (error) {
        appendLog(`❌ Failed to process ${user.username}: ${error.message}`, 'error');
        return false;
      }
    });
    return Promise.all(promises);
  };

  const processUsers = async () => {
    const invalidUsers = users.filter(user => validateUser(user));
    if (invalidUsers.length) {
      appendLog(`⚠️ Invalid users detected: ${invalidUsers.length}`, 'warning');
      return;
    }

    setProcessing(true);
    shouldStop.current = false;
    setProgress(0);

    let successCount = 0;
    let errorCount = 0;
    const totalBatches = Math.ceil(users.length / batchSize);

    appendLog(`🚀 Starting import of ${users.length} users (${totalBatches} batches)`, 'info');

    for (let i = 0; i < users.length && !shouldStop.current; i += batchSize) {
      const batch = users.slice(i, i + batchSize);
      appendLog(`🔨 Processing batch ${Math.floor(i/batchSize) + 1}/${totalBatches}`, 'info');

      const batchResults = await processBatch(batch);
      successCount += batchResults.filter(Boolean).length;
      errorCount += batchResults.filter(x => !x).length;

      setProgress(Math.round(((i + batchSize) / users.length) * 100));

      if (shouldStop.current) break;
    }

    appendLog(
      `🎉 Process ${shouldStop.current ? 'stopped' : 'completed'}. Success: ${successCount}, Errors: ${errorCount}`,
      errorCount > 0 ? 'warning' : 'success'
    );

    setProcessing(false);
  };

  const stopProcessing = () => {
    shouldStop.current = true;
    appendLog("⏹ Stopping process after current batch completes...", 'warning');
  };

  const exportUsersToCSV = async () => {
    appendLog("📦 Starting DHIS2 user export...", 'info');
    setProcessing(true);

    try {
      let seenIds = new Set();
      let usersList = [];
      let nextPage = 1;

      while (true) {
        const response = await engine.query({
          resource: 'users',
          params: {
            fields: 'id,name,username,userGroups[name],userRoles[name],lastLogin,organisationUnits[ancestors[name],name,id]',
            paging: true,
            pageSize: 1000,
            page: nextPage,
          },
        });

        const users = response.users || [];
        const pager = response.pager || {};

        for (const user of users) {
          const uid = user.id;
          if (!uid || seenIds.has(uid)) continue;
          seenIds.add(uid);

          const userGroups = user.userGroups?.map(g => g.name).join("; ") || "";
          const userRoles = user.userRoles?.map(r => r.name).join("; ") || "";
          const lastLogin = user.lastLogin || "";

          let schoolPaths = [];
          let schoolUIDs = [];

          (user.organisationUnits || []).forEach(ou => {
            if (ou.ancestors?.length === 4) {
              const path = [...ou.ancestors.map(a => a.name), ou.name].join(" > ");
              schoolPaths.push(path);
              schoolUIDs.push(ou.id || "");
            }
          });

          usersList.push({
            id: uid,
            name: user.name || '',
            username: user.username || '',
            userGroups,
            userRoles,
            lastLogin,
            schoolPath: schoolPaths.join(" | "),
            schoolUID: schoolUIDs.join(" | "),
          });
        }

        appendLog(`✅ Page ${pager.page} fetched. Total: ${seenIds.size}`, 'success');
        if (!pager.nextPage) break;
        nextPage++;
      }

      setExportedUsers(usersList);
      appendLog(`🎉 Export complete. Total users: ${seenIds.size}`, 'success');
    } catch (error) {
      appendLog(`❌ Export failed: ${error.message}`, 'error');
    } finally {
      setProcessing(false);
    }
  };

  const exportFilteredUsers = (format = 'csv') => {
    const filtered = exportedUsers.filter(u =>
      u.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
      u.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
    );

    if (filtered.length === 0) {
      appendLog('⚠️ No users match your filters', 'warning');
      return;
    }

    try {
      let content, mimeType, extension;

      if (format === 'csv') {
        const headers = availableColumns
          .filter(col => selectedColumns.includes(col.id))
          .map(col => col.label);

        const rows = filtered.map(user => 
          selectedColumns.map(col => {
            const value = user[col] || '';
            return value.includes(',') ? `"${value}"` : value;
          }).join(',')
        );

        content = [headers.join(','), ...rows].join('\n');
        mimeType = 'text/csv;charset=utf-8;';
        extension = 'csv';
      } else {
        content = JSON.stringify(filtered, null, 2);
        mimeType = 'application/json';
        extension = 'json';
      }

      downloadFile(content, `dhis2_users_${new Date().toISOString().slice(0,10)}.${extension}`, mimeType);
    } catch (error) {
      appendLog(`❌ Export error: ${error.message}`, 'error');
    }
  };

  const toggleTheme = () => setTheme(theme === 'light' ? 'dark' : 'light');
  const filteredUsers = exportedUsers.filter(user =>
    user.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
    user.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
  );
  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);

  const handleNextPage = () => currentPage < totalPages && setCurrentPage(currentPage + 1);
  const handlePrevPage = () => currentPage > 1 && setCurrentPage(currentPage - 1);

  const toggleColumn = (columnId) => {
    setSelectedColumns(prev => 
      prev.includes(columnId) ? prev.filter(id => id !== columnId) : [...prev, columnId]
    );
  };

  const getLogColor = (type) => {
    switch (type) {
      case 'error': return 'text-red-400';
      case 'warning': return 'text-yellow-400';
      case 'success': return 'text-green-400';
      default: return 'text-gray-300';
    }
  };

  if (!engine) return <Layer><CircularLoader /></Layer>;

  return (
    <div className={`min-h-screen p-4 ${theme === 'dark' ? 'dark bg-gray-900' : 'bg-gray-50'}`}>
      <div className="max-w-7xl mx-auto rounded-2xl shadow-lg overflow-hidden">
        <div className={`p-6 ${theme === 'dark' ? 'bg-gray-800' : 'bg-blue-600'} text-white`}>
          <div className="flex justify-between items-center">
            <div className="flex items-center space-x-3">
              <FiUsers className="text-2xl" />
              <h1 className="text-2xl font-bold">DHIS2 User Manager</h1>
            </div>
            <div className="flex space-x-4">
              <Button
                onClick={toggleTheme}
                className="flex items-center space-x-1 bg-black bg-opacity-20 px-3 py-1 rounded hover:bg-opacity-30 transition"
                aria-label={`Switch to ${theme === 'light' ? 'dark' : 'light'} mode`}
              >
                {theme === 'light' ? <FiMoon /> : <FiSun />}
                <span>{theme === 'light' ? 'Dark' : 'Light'} Mode</span>
              </Button>
              <Button
                onClick={() => setShowHelp(true)}
                className="flex items-center space-x-1 bg-black bg-opacity-20 px-3 py-1 rounded hover:bg-opacity-30 transition"
                aria-label="Open help"
              >
                <FiHelpCircle />
                <span>Help</span>
              </Button>
            </div>
          </div>
        </div>

        <div className={`p-6 ${theme === 'dark' ? 'bg-gray-700' : 'bg-white'}`}>
          <div className="mb-4 p-2 rounded-md bg-blue-50 border border-blue-200 dark:bg-gray-800 dark:border-gray-700">
            <div className="flex items-center">
              <span className="font-medium mr-2">Server Status:</span>
              <span className={`inline-block w-3 h-3 rounded-full mr-2 ${
                connectionStatus === 'connected' ? 'bg-green-500' : 'bg-red-500'
              }`}></span>
              <span>{connectionStatus}</span>
            </div>
            {connectionStatus === 'disconnected' && (
              <p className="mt-1 text-sm text-red-600 dark:text-red-400">
                Connection issues detected. Some operations may fail.
              </p>
            )}
          </div>

          <div className="flex border-b mb-6">
            <Button
              className={`px-4 py-2 font-medium flex items-center ${
                activeTab === 'import' 
                  ? theme === 'dark' 
                    ? 'text-blue-300 border-b-2 border-blue-300' 
                    : 'text-blue-600 border-b-2 border-blue-600' 
                  : theme === 'dark' 
                    ? 'text-gray-400' 
                    : 'text-gray-600'
              }`}
              onClick={() => setActiveTab('import')}
              aria-selected={activeTab === 'import'}
            >
              <FiUpload className="mr-2" />
              Import Users
            </Button>
            <Button
              className={`px-4 py-2 font-medium flex items-center ${
                activeTab === 'export' 
                  ? theme === 'dark' 
                    ? 'text-blue-300 border-b-2 border-blue-300' 
                    : 'text-blue-600 border-b-2 border-blue-600' 
                  : theme === 'dark' 
                    ? 'text-gray-400' 
                    : 'text-gray-600'
              }`}
              onClick={() => setActiveTab('export')}
              aria-selected={activeTab === 'export'}
            >
              <FiDownload className="mr-2" />
              Export Users
            </Button>
          </div>

          {activeTab === 'import' && (
            <div className="space-y-6">
              <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                <h2 className="text-lg font-semibold mb-3">Import Users</h2>
                <div className="flex flex-wrap gap-4">
                  <div className="flex-1 min-w-[300px]">
                    <label className="block mb-2 font-medium" htmlFor="file-upload">Upload File</label>
                    <div className="flex items-center space-x-2">
                      <label className={`flex-1 border rounded-lg p-2 cursor-pointer flex items-center ${
                        theme === 'dark' ? 'border-gray-600 bg-gray-900 hover:bg-gray-800' : 'border-gray-300 bg-white hover:bg-gray-50'
                      } transition`} htmlFor="file-upload">
                        <FiUpload className="mr-2" />
                        <span>Choose File</span>
                        <input 
                          id="file-upload"
                          type="file" 
                          accept=".json,.csv" 
                          onChange={handleFileUpload} 
                          disabled={processing}
                          className="hidden" 
                          aria-label="Upload JSON or CSV file"
                        />
                      </label>
                      <Button 
                        onClick={() => {
                          if (users.length > 0) {
                            downloadFile(JSON.stringify(users, null, 2), 'converted_users.json', 'application/json');
                          } else {
                            appendLog('⚠️ No users loaded to convert', 'warning');
                          }
                        }}
                        className={`px-4 py-2 rounded-lg flex items-center ${
                          theme === 'dark' ? 'bg-purple-700 hover:bg-purple-600' : 'bg-purple-600 hover:bg-purple-500'
                        } text-white transition`}
                        aria-label="Convert loaded users to JSON"
                      >
                        <FiDownload className="mr-2" />
                        Convert to JSON
                      </Button>
                      <div className="flex space-x-2">
                        <Button 
                          onClick={downloadSampleJson}
                          className={`px-4 py-2 rounded-lg flex items-center ${
                            theme === 'dark' ? 'bg-purple-700 hover:bg-purple-600' : 'bg-purple-600 hover:bg-purple-500'
                          } text-white transition`}
                          title="Download JSON sample"
                          aria-label="Download JSON sample"
                        >
                          <FiDownload className="mr-2" />
                          JSON
                        </Button>
                        <Button 
                          onClick={downloadSampleCSV}
                          className={`px-4 py-2 rounded-lg flex items-center ${
                            theme === 'dark' ? 'bg-blue-700 hover:bg-blue-600' : 'bg-blue-600 hover:bg-blue-500'
                          } text-white transition`}
                          title="Download CSV template"
                          aria-label="Download CSV template"
                        >
                          <FiDownload className="mr-2" />
                          CSV
                        </Button>
                      </div>
                    </div>
                    {users.length > 0 && (
                      <p className="mt-2 text-sm text-green-500">
                        ✅ {users.length} users loaded
                      </p>
                    )}
                  </div>

                  <div className="flex-1 min-w-[300px]">
                    <label className="block mb-2 font-medium" htmlFor="batch-size">Batch Settings</label>
                    <Input
                      type="number"
                      value={batchSize}
                      onChange={({ value }) => setBatchSize(Number(value))}
                      className={`w-full border rounded-lg p-2 ${
                        theme === 'dark' ? 'bg-gray-900 border-gray-600' : 'bg-white border-gray-300'
                      }`}
                      aria-label="Select batch size for user import"
                    >
                      {[1, 2, 5, 10].map(size => (
                        <option key={size} value={size}>{size} users/batch</option>
                      ))}
                    </Input>
                  </div>
                </div>
              </div>

              <div className="flex flex-wrap gap-4">
                <Button 
                  onClick={processUsers} 
                  disabled={processing || users.length === 0 || connectionStatus !== 'connected'}
                  className={`px-4 py-2 rounded-lg flex items-center ${
                    processing || users.length === 0 || connectionStatus !== 'connected'
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-green-600 hover:bg-green-500'
                  } text-white transition`}
                  aria-label="Start user import"
                >
                  <FiPlay className="mr-2" />
                  Start Import
                </Button>
                <Button 
                  onClick={stopProcessing} 
                  disabled={!processing}
                  className={`px-4 py-2 rounded-lg flex items-center ${
                    !processing 
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-red-600 hover:bg-red-500'
                  } text-white transition`}
                  aria-label="Stop user import"
                >
                  <FiSquare className="mr-2" />
                  Stop
                </Button>
              </div>

              {processing && (
                <div className="w-full bg-gray-200 rounded-full h-2.5 overflow-hidden dark:bg-gray-700">
                  <div 
                    className="bg-blue-600 h-2.5 transition-all duration-300 dark:bg-blue-400" 
                    style={{ width: `${progress}%` }} 
                    role="progressbar"
                    aria-valuenow={progress}
                    aria-valuemin={0}
                    aria-valuemax={100}
                  />
                </div>
              )}

              <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                <h3 className="font-medium mb-2">Activity Log</h3>
                <div className={`h-60 overflow-y-auto p-3 rounded ${
                  theme === 'dark' ? 'bg-black' : 'bg-gray-200'
                }`} role="log">
                  {log.length === 0 ? (
                    <p className={`text-center ${
                      theme === 'dark' ? 'text-gray-500' : 'text-gray-600'
                    }`}>
                      No activity yet
                    </p>
                  ) : (
                    <div className="font-mono text-sm space-y-1">
                      {log.map((entry, i) => (
                        <div key={i} className={getLogColor(entry.type)}>
                          <span className="opacity-70 mr-2">[{entry.timestamp}]</span>
                          {entry.message}
                        </div>
                      ))}
                      <div ref={logEndRef} />
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {activeTab === 'export' && (
            <div className="space-y-6">
              <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                <h2 className="text-lg font-semibold mb-3">Export Users</h2>
                <Button 
                  onClick={exportUsersToCSV} 
                  disabled={processing}
                  className={`px-4 py-2 rounded-lg flex items-center ${
                    processing 
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-blue-600 hover:bg-blue-500'
                  } text-white transition`}
                  aria-label="Fetch users from DHIS2"
                >
                  <FiDownload className="mr-2" />
                  Fetch Users from DHIS2
                </Button>
              </div>

              {exportedUsers.length > 0 && (
                <>
                  <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                    <h3 className="font-medium mb-3">Filter & Export</h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                      <div>
                        <label className="block mb-1" htmlFor="username-filter">Username</label>
                        <Input
                          id="username-filter"
                          type="text"
                          placeholder="Filter by username"
                          value={usernameFilter}
                          onChange={({ value }) => setUsernameFilter(value)}
                          className={`w-full p-2 rounded border ${
                            theme === 'dark' ? 'bg-gray-900 border-gray-600' : 'bg-white border-gray-300'
                          }`}
                          aria-label="Filter users by username"
                        />
                      </div>
                      <div>
                        <label className="block mb-1" htmlFor="orgunit-filter">Organization Unit</label>
                        <Input
                          id="orgunit-filter"
                          type="text"
                          placeholder="Filter by org unit path"
                          value={orgUnitFilter}
                          onChange={({ value }) => setOrgUnitFilter(value)}
                          className={`w-full p-2 rounded border ${
                            theme === 'dark' ? 'bg-gray-900 border-gray-600' : 'bg-white border-gray-300'
                          }`}
                          aria-label="Filter users by organization unit path"
                        />
                      </div>
                    </div>

                    <div className="mb-4">
                      <label className="block mb-2">Columns to Export</label>
                      <div className="flex flex-wrap gap-2">
                        {availableColumns.map(col => (
                          <label key={col.id} className="flex items-center space-x-1">
                            <input
                              type="checkbox"
                              checked={selectedColumns.includes(col.id)}
                              onChange={() => toggleColumn(col.id)}
                              className="rounded"
                              aria-label={`Toggle ${col.label} column`}
                            />
                            <span>{col.label}</span>
                          </label>
                        ))}
                      </div>
                    </div>

                    <div className="flex flex-wrap gap-3">
                      <Button 
                        onClick={() => exportFilteredUsers('csv')}
                        className={`px-4 py-2 rounded-lg flex items-center ${
                          theme === 'dark' ? 'bg-green-700 hover:bg-green-600' : 'bg-green-600 hover:bg-green-500'
                        } text-white transition`}
                        aria-label="Export filtered users as CSV"
                      >
                        <FiDownload className="mr-2" />
                        Export as CSV
                      </Button>
                      <Button 
                        onClick={() => exportFilteredUsers('json')}
                        className={`px-4 py-2 rounded-lg flex items-center ${
                          theme === 'dark' ? 'bg-yellow-700 hover:bg-yellow-600' : 'bg-yellow-600 hover:bg-yellow-500'
                        } text-white transition`}
                        aria-label="Export filtered users as JSON"
                      >
                        <FiDownload className="mr-2" />
                        Export as JSON
                      </Button>
                    </div>
                  </div>

                  <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                    <h3 className="font-medium mb-3">User Data ({filteredUsers.length} users)</h3>
                    <div className="overflow-x-auto">
                      <Table>
                        <TableHead>
                          <TableRow>
                            {selectedColumns.includes('id') && <TableHeader>ID</TableHeader>}
                            {selectedColumns.includes('name') && <TableHeader>Name</TableHeader>}
                            {selectedColumns.includes('username') && <TableHeader>Username</TableHeader>}
                            {selectedColumns.includes('userGroups') && <TableHeader>Groups</TableHeader>}
                            {selectedColumns.includes('userRoles') && <TableHeader>Roles</TableHeader>}
                            {selectedColumns.includes('lastLogin') && <TableHeader>Last Login</TableHeader>}
                            {selectedColumns.includes('schoolPath') && <TableHeader>School Path</TableHeader>}
                            {selectedColumns.includes('schoolUID') && <TableHeader>School UID</TableHeader>}
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {currentUsers.map((user, idx) => (
                            <TableRow key={idx}>
                              {selectedColumns.includes('id') && <TableCell>{user.id}</TableCell>}
                              {selectedColumns.includes('name') && <TableCell>{user.name}</TableCell>}
                              {selectedColumns.includes('username') && <TableCell>{user.username}</TableCell>}
                              {selectedColumns.includes('userGroups') && <TableCell>{user.userGroups}</TableCell>}
                              {selectedColumns.includes('userRoles') && <TableCell>{user.userRoles}</TableCell>}
                              {selectedColumns.includes('lastLogin') && <TableCell>{user.lastLogin}</TableCell>}
                              {selectedColumns.includes('schoolPath') && <TableCell>{user.schoolPath}</TableCell>}
                              {selectedColumns.includes('schoolUID') && <TableCell>{user.schoolUID}</TableCell>}
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </div>

                    {totalPages > 1 && (
                      <div className="mt-4 flex justify-between items-center">
                        <Button 
                          onClick={handlePrevPage} 
                          disabled={currentPage === 1}
                          className={`px-4 py-2 rounded ${
                            currentPage === 1 
                              ? 'opacity-50 cursor-not-allowed' 
                              : theme === 'dark' 
                                ? 'bg-gray-600 hover:bg-gray-500' 
                                : 'bg-gray-200 hover:bg-gray-300'
                          } transition`}
                          aria-label="Previous page"
                        >
                          Previous
                        </Button>
                        <span aria-label={`Page ${currentPage} of ${totalPages}`}>
                          Page {currentPage} of {totalPages}
                        </span>
                        <Button 
                          onClick={handleNextPage} 
                          disabled={currentPage === totalPages}
                          className={`px-4 py-2 rounded ${
                            currentPage === totalPages 
                              ? 'opacity-50 cursor-not-allowed' 
                              : theme === 'dark' 
                                ? 'bg-gray-600 hover:bg-gray-500' 
                                : 'bg-gray-200 hover:bg-gray-300'
                          } transition`}
                          aria-label="Next page"
                        >
                          Next
                        </Button>
                      </div>
                    )}
                  </div>
                </>
              )}

              <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                <h3 className="font-medium mb-2">Activity Log</h3>
                <div className={`h-60 overflow-y-auto p-3 rounded ${
                  theme === 'dark' ? 'bg-black' : 'bg-gray-200'
                }`} role="log">
                  {log.length === 0 ? (
                    <p className={`text-center ${
                      theme === 'dark' ? 'text-gray-500' : 'text-gray-600'
                    }`}>
                      No activity yet
                    </p>
                  ) : (
                    <div className="font-mono text-sm space-y-1">
                      {log.map((entry, i) => (
                        <div key={i} className={getLogColor(entry.type)}>
                          <span className="opacity-70 mr-2">[{entry.timestamp}]</span>
                          {entry.message}
                        </div>
                      ))}
                      <div ref={logEndRef} />
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {showHelp && (
            <Layer onClick={() => setShowHelp(false)}>
              <div className={`p-6 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'}`}>
                <h2 className="text-lg font-bold mb-4">Help</h2>
                <p className="mb-4">
                  The DHIS2 User Manager allows you to import and export user data in JSON or CSV formats.
                  Upload a file to import users, or fetch and filter users from the DHIS2 server for export.
                </p>
                <Button 
                  onClick={() => setShowHelp(false)}
                  className={`px-4 py-2 rounded ${
                    theme === 'dark' ? 'bg-blue-700 hover:bg-blue-600' : 'bg-blue-600 hover:bg-blue-500'
                  } text-white transition`}
                  aria-label="Close help modal"
                >
                  Close
                </Button>
              </div>
            </Layer>
          )}
        </div>
      </div>
    </div>
  );
};

export default init({ apiVersion: 37 })(MyApp);
