import React, { useState, useRef } from 'react';
import axios from 'axios';

const BASE_URL = "https://emis.dhis2nigeria.org.ng/dhis/api";
const HEADERS = {
  'Content-Type': 'application/json',
  'Authorization': 'ApiToken d2pat_DAULytLK7GUBSlfknZ6ffVxIfifC8T9c3885349470'
};

function App() {
  const [users, setUsers] = useState([]);
  const [log, setLog] = useState([]);
  const [processing, setProcessing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [exportedUsers, setExportedUsers] = useState([]);
  const [usernameFilter, setUsernameFilter] = useState('');
  const [orgUnitFilter, setOrgUnitFilter] = useState('');
  const shouldStop = useRef(false);

  const appendLog = (message) => {
    setLog(log => [...log, message]);
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onload = event => {
      const data = JSON.parse(event.target.result);
      setUsers(data);
      appendLog(`‚úÖ Loaded ${data.length} users`);
    };

    reader.readAsText(file);
  };

  const getUserId = async (username) => {
    try {
      const res = await axios.get(`${BASE_URL}/users?filter=username:eq:${username}&fields=id`, { headers: HEADERS });
      return res.data.users?.[0]?.id || null;
    } catch {
      return null;
    }
  };

  const updateUser = async (user, id) => {
    const copy = { ...user };
    delete copy.password;
    await axios.put(`${BASE_URL}/users/${id}`, copy, { headers: HEADERS });
    appendLog(`üîÑ Updated user: ${user.username}`);
  };

  const createUser = async (user) => {
    try {
      await axios.post(`${BASE_URL}/users`, user, { headers: HEADERS });
      appendLog(`üÜï Created user: ${user.username}`);
    } catch (error) {
      if (error.response?.status === 409) {
        const id = await getUserId(user.username);
        if (id) {
          appendLog(`‚ö†Ô∏è Conflict: Updating user ${user.username}`);
          await updateUser(user, id);
        } else {
          appendLog(`‚ùå Conflict and ID not found: ${user.username}`);
        }
      } else {
        appendLog(`‚ùå Error for ${user.username}: ${error.message}`);
      }
    }
  };

  const processUsers = async () => {
    setProcessing(true);
    shouldStop.current = false;
    setProgress(0);
    setLog([]);

    for (let i = 0; i < users.length; i++) {
      if (shouldStop.current) {
        appendLog("üõë Process stopped by user.");
        break;
      }

      const user = users[i];
      const id = await getUserId(user.username);

      if (id) {
        await updateUser(user, id);
      } else {
        await createUser(user);
      }

      setProgress(Math.round(((i + 1) / users.length) * 100));
    }

    setProcessing(false);
  };

  const stopProcessing = () => {
    shouldStop.current = true;
  };

  const exportUsersToCSV = async () => {
    appendLog("üì¶ Starting DHIS2 user export...");

    const fieldnames = ["id", "name", "username", "userGroups", "userRoles", "lastLogin", "schoolPath", "schoolUID"];
    let seenIds = new Set();
    let usersCSV = [fieldnames.join(",")];
    let usersList = [];
    let nextUrl = `${BASE_URL}/users.json?fields=id,name,username,userGroups[name],userRoles[name],lastLogin,organisationUnits[ancestors[name],name,id]&paging=true&pageSize=1000`;

    while (nextUrl) {
      try {
        const response = await axios.get(nextUrl, { headers: HEADERS });
        if (response.status !== 200) break;

        const data = response.data;
        const users = data.users || [];
        const pager = data.pager || {};

        for (const user of users) {
          const uid = user.id;
          if (!uid || seenIds.has(uid)) continue;
          seenIds.add(uid);

          const userGroups = user.userGroups?.map(g => g.name).join("; ") || "";
          const userRoles = user.userRoles?.map(r => r.name).join("; ") || "";
          const lastLogin = user.lastLogin || "";

          let schoolPaths = [];
          let schoolUIDs = [];

          (user.organisationUnits || []).forEach(ou => {
            if (ou.ancestors?.length === 4) {
              const path = [...ou.ancestors.map(a => a.name), ou.name].join(" > ");
              schoolPaths.push(path);
              schoolUIDs.push(ou.id || "");
            }
          });

          const row = [
            uid,
            `"${user.name || ""}"`,
            user.username || "",
            `"${userGroups}"`,
            `"${userRoles}"`,
            lastLogin,
            `"${schoolPaths.join(" | ")}"`,
            `"${schoolUIDs.join(" | ")}"`
          ].join(",");

          usersCSV.push(row);

          usersList.push({
            id: uid,
            name: user.name || '',
            username: user.username || '',
            userGroups,
            userRoles,
            lastLogin,
            schoolPath: schoolPaths.join(" | "),
            schoolUID: schoolUIDs.join(" | ")
          });
        }

        appendLog(`‚úÖ Page ${pager.page} fetched. Total: ${seenIds.size}`);
        nextUrl = pager.nextPage || null;

      } catch (err) {
        appendLog(`‚ùå Error fetching users: ${err.message}`);
        break;
      }
    }

    const blob = new Blob([usersCSV.join("\n")], { type: "text/csv;charset=utf-8;" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.setAttribute("download", `dhis2_users_export_${new Date().toISOString().slice(0,10)}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    setExportedUsers(usersList);
    appendLog(`üéâ Export complete. Total users: ${seenIds.size}`);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-6xl mx-auto bg-white rounded-2xl shadow p-6">
        <h1 className="text-2xl font-bold mb-4 text-blue-700">DHIS2 User Manager</h1>

        <input
          type="file"
          accept=".json"
          onChange={handleFileUpload}
          disabled={processing}
          className="mb-4 w-full border border-gray-300 rounded p-2"
        />

        <div className="flex space-x-4 mb-4">
          <button
            onClick={processUsers}
            disabled={processing || users.length === 0}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Start Import
          </button>
          <button
            onClick={stopProcessing}
            disabled={!processing}
            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
          >
            Stop
          </button>
          <button
            onClick={exportUsersToCSV}
            disabled={processing}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Export Users to CSV
          </button>
        </div>

        <div className="w-full bg-gray-200 rounded h-4 overflow-hidden mb-4">
          <div className="bg-blue-500 h-full transition-all duration-300" style={{ width: `${progress}%` }} />
        </div>

        <div className="bg-black text-white p-4 rounded h-48 overflow-auto text-sm font-mono whitespace-pre-wrap mb-6">
          {log.join("\n")}
        </div>

        {exportedUsers.length > 0 && (
          <>
            <div className="flex gap-4 mb-4">
              <input
                type="text"
                placeholder="Search by username"
                className="border border-gray-300 rounded p-2 w-1/2"
                value={usernameFilter}
                onChange={(e) => setUsernameFilter(e.target.value)}
              />
              <input
                type="text"
                placeholder="Search by org unit"
                className="border border-gray-300 rounded p-2 w-1/2"
                value={orgUnitFilter}
                onChange={(e) => setOrgUnitFilter(e.target.value)}
              />
            </div>

            <div className="overflow-auto max-h-[400px] border rounded">
              <table className="min-w-full text-sm text-left">
                <thead className="bg-gray-200 sticky top-0">
                  <tr>
                    <th className="px-2 py-1">Username</th>
                    <th className="px-2 py-1">Name</th>
                    <th className="px-2 py-1">Org Unit</th>
                    <th className="px-2 py-1">User Groups</th>
                    <th className="px-2 py-1">User Roles</th>
                    <th className="px-2 py-1">Last Login</th>
                  </tr>
                </thead>
                <tbody>
                  {exportedUsers
                    .filter(user =>
                      user.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
                      user.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
                    )
                    .map((user, i) => (
                      <tr key={i} className="border-t hover:bg-gray-50">
                        <td className="px-2 py-1">{user.username}</td>
                        <td className="px-2 py-1">{user.name}</td>
                        <td className="px-2 py-1">{user.schoolPath}</td>
                        <td className="px-2 py-1">{user.userGroups}</td>
                        <td className="px-2 py-1">{user.userRoles}</td>
                        <td className="px-2 py-1">{user.lastLogin || '‚Äî'}</td>
                      </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
