// src/App.js
import React, { useState, useRef } from 'react';
import axios from 'axios';

const BASE_URL = "https://emis.dhis2nigeria.org.ng/dhis/api";
const HEADERS = {
  'Content-Type': 'application/json',
  'Authorization': 'ApiToken d2pat_DAULytLK7GUBSlfknZ6ffVxIfifC8T9c3885349470'
};

function App() {
  const [users, setUsers] = useState([]);
  const [log, setLog] = useState([]);
  const [processing, setProcessing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [exportedUsers, setExportedUsers] = useState([]);
  const [usernameFilter, setUsernameFilter] = useState('');
  const [orgUnitFilter, setOrgUnitFilter] = useState('');
  const [theme, setTheme] = useState('light');
  const [currentPage, setCurrentPage] = useState(1);
  const usersPerPage = 10;
  const shouldStop = useRef(false);

  const appendLog = (message) => {
    setLog(log => [...log, message]);
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onload = event => {
      const data = JSON.parse(event.target.result);
      setUsers(data);
      appendLog(`âœ… Loaded ${data.length} users`);
    };

    reader.readAsText(file);
  };

  const getUserId = async (username) => {
    try {
      const res = await axios.get(`${BASE_URL}/users?filter=username:eq:${username}&fields=id`, { headers: HEADERS });
      return res.data.users?.[0]?.id || null;
    } catch {
      return null;
    }
  };

  const updateUser = async (user, id) => {
    const copy = { ...user };
    delete copy.password;
    await axios.put(`${BASE_URL}/users/${id}`, copy, { headers: HEADERS });
    appendLog(`ðŸ”„ Updated user: ${user.username}`);
  };

  const createUser = async (user) => {
    try {
      await axios.post(`${BASE_URL}/users`, user, { headers: HEADERS });
      appendLog(`ðŸ†• Created user: ${user.username}`);
    } catch (error) {
      if (error.response?.status === 409) {
        const id = await getUserId(user.username);
        if (id) {
          appendLog(`âš ï¸ Conflict: Updating user ${user.username}`);
          await updateUser(user, id);
        } else {
          appendLog(`âŒ Conflict and ID not found: ${user.username}`);
        }
      } else {
        appendLog(`âŒ Error for ${user.username}: ${error.message}`);
      }
    }
  };

  const processUsers = async () => {
    setProcessing(true);
    shouldStop.current = false;
    setProgress(0);
    setLog([]);

    for (let i = 0; i < users.length; i++) {
      if (shouldStop.current) {
        appendLog("ðŸ›‘ Process stopped by user.");
        break;
      }

      const user = users[i];
      const id = await getUserId(user.username);

      if (id) {
        await updateUser(user, id);
      } else {
        await createUser(user);
      }

      setProgress(Math.round(((i + 1) / users.length) * 100));
    }

    setProcessing(false);
  };

  const stopProcessing = () => {
    shouldStop.current = true;
  };

  const exportUsersToCSV = async () => {
    appendLog("ðŸ“¦ Starting DHIS2 user export...");

    const fieldnames = ["id", "name", "username", "userGroups", "userRoles", "lastLogin", "schoolPath", "schoolUID"];
    let seenIds = new Set();
    let usersCSV = [fieldnames.join(",")];
    let usersList = [];
    let nextUrl = `${BASE_URL}/users.json?fields=id,name,username,userGroups[name],userRoles[name],lastLogin,organisationUnits[ancestors[name],name,id]&paging=true&pageSize=1000`;

    while (nextUrl) {
      try {
        const response = await axios.get(nextUrl, { headers: HEADERS });
        if (response.status !== 200) break;

        const data = response.data;
        const users = data.users || [];
        const pager = data.pager || {};

        for (const user of users) {
          const uid = user.id;
          if (!uid || seenIds.has(uid)) continue;
          seenIds.add(uid);

          const userGroups = user.userGroups?.map(g => g.name).join("; ") || "";
          const userRoles = user.userRoles?.map(r => r.name).join("; ") || "";
          const lastLogin = user.lastLogin || "";

          let schoolPaths = [];
          let schoolUIDs = [];

          (user.organisationUnits || []).forEach(ou => {
            if (ou.ancestors?.length === 4) {
              const path = [...ou.ancestors.map(a => a.name), ou.name].join(" > ");
              schoolPaths.push(path);
              schoolUIDs.push(ou.id || "");
            }
          });

          const row = [
            uid,
            `"${user.name || ""}"`,
            user.username || "",
            `"${userGroups}"`,
            `"${userRoles}"`,
            lastLogin,
            `"${schoolPaths.join(" | ")}"`,
            `"${schoolUIDs.join(" | ")}"`
          ].join(",");

          usersCSV.push(row);

          usersList.push({
            id: uid,
            name: user.name || '',
            username: user.username || '',
            userGroups,
            userRoles,
            lastLogin,
            schoolPath: schoolPaths.join(" | "),
            schoolUID: schoolUIDs.join(" | ")
          });
        }

        appendLog(`âœ… Page ${pager.page} fetched. Total: ${seenIds.size}`);
        nextUrl = pager.nextPage || null;

      } catch (err) {
        appendLog(`âŒ Error fetching users: ${err.message}`);
        break;
      }
    }

    const blob = new Blob([usersCSV.join("\n")], { type: "text/csv;charset=utf-8;" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.setAttribute("download", `dhis2_users_export_${new Date().toISOString().slice(0,10)}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    setExportedUsers(usersList);
    appendLog(`ðŸŽ‰ Export complete. Total users: ${seenIds.size}`);
    setTimeout(() => setLog([]), 5000);
  };

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  const filteredUsers = exportedUsers.filter(user =>
    user.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
    user.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
  );

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);

  return (
    <div className={`${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-100 text-black'} min-h-screen p-6`}>
      <div className="max-w-6xl mx-auto rounded-2xl shadow p-6 bg-white dark:bg-gray-800">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-2xl font-bold text-blue-700 dark:text-blue-300">DHIS2 User Manager</h1>
          <button onClick={toggleTheme} className="text-sm text-gray-600 dark:text-gray-300 hover:underline">
            Toggle {theme === 'light' ? 'Dark' : 'Light'} Mode
          </button>
        </div>

        {/* Remaining UI components like file upload, buttons, progress bar, log viewer, and user table with pagination */}
      </div>
    </div>
  );
}

export default App;
