// src/App.js
import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const BASE_URL = "https://emis.dhis2nigeria.org.ng/dhis/api";
const HEADERS = {
  'Content-Type': 'application/json',
  'Authorization': 'ApiToken d2pat_DAULytLK7GUBSlfknZ6ffVxIfifC8T9c3885349470'
};

function App() {
  const [users, setUsers] = useState([]);
  const [processing, setProcessing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [exportedUsers, setExportedUsers] = useState([]);
  const [usernameFilter, setUsernameFilter] = useState('');
  const [orgUnitFilter, setOrgUnitFilter] = useState('');
  const [theme, setTheme] = useState('light');
  const [currentPage, setCurrentPage] = useState(1);
  const [loadingExport, setLoadingExport] = useState(true);
  const usersPerPage = 10;
  const shouldStop = useRef(false);

  useEffect(() => {
    document.documentElement.classList.toggle('dark', theme === 'dark');
  }, [theme]);

  useEffect(() => {
    exportUsers();
  }, []);

  const exportUsers = async () => {
    const seenIds = new Set();
    let usersList = [];

    try {
      const firstPage = await axios.get(`${BASE_URL}/users.json?fields=id,name,username,userGroups[name],userRoles[name],lastLogin,organisationUnits[ancestors[name],name,id]&paging=true&pageSize=1000&page=1`, { headers: HEADERS });
      const total = firstPage.data.pager?.total || 1;
      const pageSize = 1000;
      const totalPages = Math.ceil(total / pageSize);

      const pageUrls = [];
      for (let i = 1; i <= totalPages; i++) {
        pageUrls.push(`${BASE_URL}/users.json?fields=id,name,username,userGroups[name],userRoles[name],lastLogin,organisationUnits[ancestors[name],name,id]&paging=true&pageSize=${pageSize}&page=${i}`);
      }

      const chunkSize = 5;
      for (let i = 0; i < pageUrls.length; i += chunkSize) {
        const chunk = pageUrls.slice(i, i + chunkSize);
        const responses = await Promise.all(chunk.map(url => axios.get(url, { headers: HEADERS })));

        responses.forEach(res => {
          (res.data.users || []).forEach(user => {
            const uid = user.id;
            if (!uid || seenIds.has(uid)) return;
            seenIds.add(uid);

            const userGroups = user.userGroups?.map(g => g.name).join("; ") || "";
            const userRoles = user.userRoles?.map(r => r.name).join("; ") || "";
            const lastLogin = user.lastLogin || "";

            let schoolPaths = [];
            let schoolUIDs = [];

            (user.organisationUnits || []).forEach(ou => {
              if (ou.ancestors?.length === 4) {
                const path = [...ou.ancestors.map(a => a.name), ou.name].join(" > ");
                schoolPaths.push(path);
                schoolUIDs.push(ou.id || "");
              }
            });

            usersList.push({
              id: uid,
              name: user.name || '',
              username: user.username || '',
              userGroups,
              userRoles,
              lastLogin,
              schoolPath: schoolPaths.join(" | "),
              schoolUID: schoolUIDs.join(" | ")
            });
          });
        });

        const completed = Math.min(i + chunk.length, totalPages);
        setProgress(Math.round((completed / totalPages) * 100));
      }
    } catch (error) {
      console.error("Error fetching users:", error);
    }

    setExportedUsers(usersList);
    setUsers(usersList);
    setLoadingExport(false);
    setProgress(100);
  };

  const exportFilteredUsersToCSV = () => {
    const filtered = exportedUsers.filter(u =>
      u.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
      u.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
    );

    const headers = ["id", "name", "username", "userGroups", "userRoles", "lastLogin", "schoolPath", "schoolUID"];
    const rows = filtered.map(user => [
      user.id,
      `"${user.name}"`,
      user.username,
      `"${user.userGroups}"`,
      `"${user.userRoles}"`,
      user.lastLogin,
      `"${user.schoolPath}"`,
      `"${user.schoolUID}"`
    ].join(","));

    const csvContent = [headers.join(","), ...rows].join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.setAttribute("download", `filtered_users_${new Date().toISOString().slice(0,10)}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const exportAllUsersToCSV = () => {
    const headers = ["id", "name", "username", "userGroups", "userRoles", "lastLogin", "schoolPath", "schoolUID"];
    const rows = exportedUsers.map(user => [
      user.id,
      `"${user.name}"`,
      user.username,
      `"${user.userGroups}"`,
      `"${user.userRoles}"`,
      user.lastLogin,
      `"${user.schoolPath}"`,
      `"${user.schoolUID}"`
    ].join(","));

    const csvContent = [headers.join(","), ...rows].join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.setAttribute("download", `all_users_${new Date().toISOString().slice(0,10)}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const filteredUsers = exportedUsers.filter(user =>
    user.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
    user.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
  );

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);

  const handleNextPage = () => {
    if (currentPage < totalPages) setCurrentPage(currentPage + 1);
  };

  const handlePrevPage = () => {
    if (currentPage > 1) setCurrentPage(currentPage - 1);
  };

  return (
    <div className={`${theme === 'dark' ? 'dark' : ''} min-h-screen p-6`}>  
      <div className="max-w-6xl mx-auto rounded-2xl shadow p-6 bg-gray-100 text-black dark:bg-gray-900 dark:text-white">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-2xl font-bold text-blue-700 dark:text-blue-300">DHIS2 User Manager</h1>
          <button onClick={() => setTheme(theme === 'light' ? 'dark' : 'light')} className="text-sm text-gray-600 dark:text-gray-300 hover:underline">
            Toggle {theme === 'light' ? 'Dark' : 'Light'} Mode
          </button>
        </div>

        {loadingExport ? (
          <div className="flex flex-col items-center justify-center h-96">
            <div className="h-12 w-12 border-4 border-blue-500 border-t-transparent border-solid rounded-full animate-spin"></div>
            <p className="mt-4 text-lg font-medium">Loading Users... {progress}%</p>
          </div>
        ) : (
          <>
            <div className="flex flex-wrap gap-4 mb-4">
              <input type="file" accept=".json" onChange={(e) => {
                const file = e.target.files[0];
                const reader = new FileReader();
                reader.onload = event => {
                  const data = JSON.parse(event.target.result);
                  setUsers(data);
                };
                reader.readAsText(file);
              }} className="border p-2 rounded" />
              <button onClick={() => {}} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">Start Import</button>
              <button onClick={() => {}} className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700">Stop</button>
              <button onClick={exportAllUsersToCSV} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Export Users to CSV</button>
              <button onClick={exportFilteredUsersToCSV} className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700">Export Filtered Data</button>
            </div>

            <div className="flex gap-4 mb-4">
              <input
                type="text"
                placeholder="Filter by Username"
                value={usernameFilter}
                onChange={(e) => setUsernameFilter(e.target.value)}
                className="p-2 border rounded w-64"
              />
              <input
                type="text"
                placeholder="Filter by Org Unit"
                value={orgUnitFilter}
                onChange={(e) => setOrgUnitFilter(e.target.value)}
                className="p-2 border rounded w-64"
              />
            </div>

            <table className="min-w-full mt-4 border border-gray-300">
              <thead>
                <tr className="bg-gray-100 dark:bg-gray-700">
                  <th className="border p-2">ID</th>
                  <th className="border p-2">Name</th>
                  <th className="border p-2">Username</th>
                  <th className="border p-2">Groups</th>
                  <th className="border p-2">Roles</th>
                  <th className="border p-2">Last Login</th>
                  <th className="border p-2">School Path</th>
                </tr>
              </thead>
              <tbody>
                {currentUsers.map((user, idx) => (
                  <tr key={idx} className="even:bg-gray-50 dark:even:bg-gray-800">
                    <td className="border p-2 text-xs">{user.id}</td>
                    <td className="border p-2 text-xs">{user.name}</td>
                    <td className="border p-2 text-xs">{user.username}</td>
                    <td className="border p-2 text-xs">{user.userGroups}</td>
                    <td className="border p-2 text-xs">{user.userRoles}</td>
                    <td className="border p-2 text-xs">{user.lastLogin}</td>
                    <td className="border p-2 text-xs">{user.schoolPath}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            <div className="mt-4 flex justify-between items-center">
              <button onClick={handlePrevPage} disabled={currentPage === 1} className="px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded disabled:opacity-50">Previous</button>
              <span>Page {currentPage} of {totalPages}</span>
              <button onClick={handleNextPage} disabled={currentPage === totalPages} className="px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded disabled:opacity-50">Next</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
