import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { FiUpload, FiDownload, FiPlay, FiSquare, FiSun, FiMoon, FiHelpCircle, FiUsers } from 'react-icons/fi';

// Configure axios defaults
axios.defaults.timeout = 30000; // 30 second timeout
axios.defaults.maxContentLength = 50 * 1024 * 1024; // 50MB
axios.defaults.maxBodyLength = 50 * 1024 * 1024; // 50MB

const BASE_URL = "https://emis.dhis2nigeria.org.ng/dhis/api";
const HEADERS = {
  'Content-Type': 'application/json',
  'Authorization': 'ApiToken d2pat_DAULytLK7GUBSlfknZ6ffVxIfifC8T9c3885349470'
};

// Sample user data for download
const SAMPLE_USERS = [
  {
    "firstName": "PVT",
    "surname": "Royal",
    "username": 12020407,
    "password": "Egor123@",
    "userRoles": [
      {
        "id": "KBkjSGFKSKI"
      },
      {
        "id": "oO6BBApzmHZ"
      }
    ],
    "organisationUnits": [
      {
        "id": "wRQIw3NMW29"
      }
    ],
    "dataViewOrganisationUnits": [
      {
        "id": "wRQIw3NMW29"
      }
    ],
    "teiSearchOrganisationUnits": [
      {
        "id": "wRQIw3NMW29"
      }
    ],
    "userGroups": [
      {
        "id": "U8WuGyxfFFp"
      }
    ]
  },
  {
    "firstName": "PVT",
    "surname": "Dominion",
    "username": 12020409,
    "password": "Egor123@",
    "userRoles": [
      {
        "id": "KBkjSGFKSKI"
      },
      {
        "id": "oO6BBApzmHZ"
      }
    ],
    "organisationUnits": [
      {
        "id": "AVEyJypVCjJ"
      }
    ],
    "dataViewOrganisationUnits": [
      {
        "id": "AVEyJypVCjJ"
      }
    ],
    "teiSearchOrganisationUnits": [
      {
        "id": "AVEyJypVCjJ"
      }
    ],
    "userGroups": [
      {
        "id": "U8WuGyxfFFp"
      }
    ]
  }
];

function App() {
  // State declarations
  const [users, setUsers] = useState([]);
  const [log, setLog] = useState([]);
  const [processing, setProcessing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [exportedUsers, setExportedUsers] = useState([]);
  const [usernameFilter, setUsernameFilter] = useState('');
  const [orgUnitFilter, setOrgUnitFilter] = useState('');
  const [theme, setTheme] = useState('dark');
  const [currentPage, setCurrentPage] = useState(1);
  const [activeTab, setActiveTab] = useState('import');
  const [batchSize, setBatchSize] = useState(2);
  const [connectionStatus, setConnectionStatus] = useState('checking...');
  const [selectedColumns, setSelectedColumns] = useState([
    'id', 'name', 'username', 'userGroups', 'userRoles', 'lastLogin', 'schoolPath'
  ]);

  const shouldStop = useRef(false);
  const logEndRef = useRef(null);

  const usersPerPage = 100;
  const availableColumns = [
    { id: 'id', label: 'ID' },
    { id: 'name', label: 'Name' },
    { id: 'username', label: 'Username' },
    { id: 'userGroups', label: 'Groups' },
    { id: 'userRoles', label: 'Roles' },
    { id: 'lastLogin', label: 'Last Login' },
    { id: 'schoolPath', label: 'School Path' },
    { id: 'schoolUID', label: 'School UID' }
  ];

  // Define appendLog first
  const appendLog = (message, type = 'info') => {
    const timestamp = new Date().toLocaleTimeString();
    setLog(prevLog => [...prevLog, { message, type, timestamp }]);
  };

  // Enhanced API request with retries
  const apiRequest = async (config, attempt = 1) => {
    const maxRetries = 3;
    const baseDelay = 1000;

    try {
      const response = await axios({
        ...config,
        timeout: 30000,
        headers: { ...HEADERS, ...config.headers }
      });
      return response;
    } catch (error) {
      if (attempt >= maxRetries) throw error;

      const retryDelay = baseDelay * Math.pow(2, attempt - 1);
      appendLog(`âš ï¸ Attempt ${attempt} failed, retrying in ${retryDelay/1000}s...`, 'warning');
      await new Promise(resolve => setTimeout(resolve, retryDelay));
      return apiRequest(config, attempt + 1);
    }
  };

  // Connection monitoring
  useEffect(() => {
    const checkConnection = async () => {
      try {
        await apiRequest({ method: 'get', url: `${BASE_URL}/system/ping` });
        setConnectionStatus('connected');
      } catch {
        setConnectionStatus('disconnected');
      }
    };

    checkConnection();
    const interval = setInterval(checkConnection, 30000);
    return () => clearInterval(interval);
  }, []);

  // Auto-scroll log to bottom
  useEffect(() => {
    logEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [log]);

  // File upload handler
  const handleFileUpload = (e) => {
  const file = e.target.files[0];
  if (!file) return;

  const reader = new FileReader();
  reader.onloadstart = () => appendLog('â³ Loading user file...', 'info');
  reader.onload = event => {
    try {
      if (file.name.endsWith('.csv')) {
        // Parse CSV file
        const csvData = event.target.result;
        const lines = csvData.split('\n').filter(line => line.trim());
        if (lines.length < 2) throw new Error('CSV file must contain at least one data row');

        const headers = lines[0].split(',').map(h => h.trim());
        const users = [];

        for (let i = 1; i < lines.length; i++) {
          const line = lines[i];
          const values = line.split(',').map(v => v.trim().replace(/^"|"$/g, ''));
          const user = {};

          headers.forEach((header, idx) => {
            if (values[idx]) {
              try {
                // Try to parse array fields as JSON, fallback to raw value
                user[header] = header.includes('Roles') || header.includes('Units') || header.includes('Groups') 
                  ? safeJsonParse(values[idx]) 
                  : values[idx];
              } catch (error) {
                user[header] = values[idx];
              }
            }
          });

          users.push(user);
        }

        setUsers(users);
        appendLog(`âœ… Loaded ${users.length} users from CSV`, 'success');
      } else {
        // Parse JSON file
        const data = JSON.parse(event.target.result);
        if (!Array.isArray(data)) throw new Error('File should contain an array of users');
        setUsers(data);
        appendLog(`âœ… Loaded ${data.length} users from JSON`, 'success');
      }
    } catch (error) {
      appendLog(`âŒ Error parsing file: ${error.message}`, 'error');
    }
  };
  reader.onerror = () => appendLog('âŒ Failed to read file', 'error');
  reader.readAsText(file);
};

// Helper function for safe JSON parsing
const safeJsonParse = (str) => {
  try {
    return JSON.parse(str);
  } catch (e) {
    // If parsing fails, try to handle common CSV array formats
    if (str.startsWith('[') && str.endsWith(']')) {
      return str.slice(1, -1).split(';').map(item => ({ id: item.trim() }));
    }
    return str; // Return as-is if we can't parse
  }
};

  // Sample JSON download
  const downloadSampleJson = () => {
    try {
      const blob = new Blob([JSON.stringify(SAMPLE_USERS, null, 2)], { type: 'application/json' });
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.download = 'dhis2_users_sample.json';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      appendLog('âœ… Downloaded sample JSON file', 'success');
    } catch (error) {
      appendLog(`âŒ Error downloading sample: ${error.message}`, 'error');
    }
  };

  // Sample CSV template download
  const downloadSampleCSV = () => {
    try {
      // CSV header row
      const headers = [
        'firstName',
        'surname',
        'username',
        'password',
        'userRoles',
        'organisationUnits',
        'dataViewOrganisationUnits',
        'teiSearchOrganisationUnits',
        'userGroups'
      ].join(',');

      // Sample data rows (empty except for one example row)
      const sampleRow = [
        'JSS', // firstName
        'Com', // surname
        '11010051', // username
        'Abakaliki123@', // password
        '[{"id": "KBkjSGFKSKI"},{"id": "oO6BBApzmHZ"}]', // userRoles
        '[{"id": "wMcSLrAHchs"}]', // organisationUnits
        '[{"id": "wMcSLrAHchs"}]', // dataViewOrganisationUnits
        '[{"id": "wMcSLrAHchs"}]', // teiSearchOrganisationUnits
        '[{"id": "Ca67o1mgiPn"}]' // userGroups
      ].map(field => `"${field}"`).join(',');

      // Combine header and sample row
      const csvContent = [headers, sampleRow].join('\n');

      const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.download = 'dhis2_users_template.csv';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      appendLog('âœ… Downloaded sample CSV template', 'success');
    } catch (error) {
      appendLog(`âŒ Error downloading CSV template: ${error.message}`, 'error');
    }
  };

  // User management functions
  const getUserId = async (username) => {
    try {
      const res = await apiRequest({
        method: 'get',
        url: `${BASE_URL}/users?filter=username:eq:${username}&fields=id`
      });
      return res.data.users?.[0]?.id || null;
    } catch (error) {
      appendLog(`âš ï¸ Error fetching user ID for ${username}: ${error.message}`, 'warning');
      return null;
    }
  };

  const updateUser = async (user, id) => {
  try {
    // Create clean copy without password
    const payload = {
      firstName: user.firstName,
      surname: user.surname,
      username: user.username,
      userRoles: user.userRoles.map(role => ({ id: role.id })),
      organisationUnits: user.organisationUnits.map(ou => ({ id: ou.id })),
      dataViewOrganisationUnits: user.dataViewOrganisationUnits.map(ou => ({ id: ou.id })),
      teiSearchOrganisationUnits: user.teiSearchOrganisationUnits.map(ou => ({ id: ou.id })),
      userGroups: user.userGroups.map(group => ({ id: group.id }))
    };

    // Debug log - remove after testing
    console.log('Update Payload:', JSON.stringify(payload, null, 2));

    const response = await apiRequest({
      method: 'put',
      url: `${BASE_URL}/users/${id}`,
      data: payload
    });

    appendLog(`ðŸ”„ Updated user: ${user.username}`, 'success');
    return true;
  } catch (error) {
    appendLog(`âŒ Error updating user ${user.username}: ${error.response?.data?.message || error.message}`, 'error');
    return false;
  }
};

  const createUser = async (user) => {
    try {
      await apiRequest({
        method: 'post',
        url: `${BASE_URL}/users`,
        data: user
      });
      appendLog(`ðŸ†• Created user: ${user.username}`, 'success');
      return true;
    } catch (error) {
      if (error.response?.status === 409) {
        const id = await getUserId(user.username);
        if (id) {
          appendLog(`âš ï¸ Conflict: Updating user ${user.username}`, 'warning');
          return await updateUser(user, id);
        } else {
          appendLog(`âŒ Conflict and ID not found: ${user.username}`, 'error');
          return false;
        }
      }
      appendLog(`âŒ Error for ${user.username}: ${error.message}`, 'error');
      return false;
    }
  };

  // Processing functions
  const processBatch = async (batch) => {
    const results = [];
    for (const user of batch) {
      try {
        await new Promise(resolve => setTimeout(resolve, 500));
        const id = await getUserId(user.username);
        const result = id ? await updateUser(user, id) : await createUser(user);
        results.push(result);
      } catch (error) {
        appendLog(`âŒ Failed to process ${user.username}: ${error.message}`, 'error');
        results.push(false);
      }
    }
    return results;
  };

  const processUsers = async () => {
    setProcessing(true);
    shouldStop.current = false;
    setProgress(0);

    let successCount = 0;
    let errorCount = 0;
    const totalBatches = Math.ceil(users.length / batchSize);

    appendLog(`ðŸš€ Starting import of ${users.length} users (${totalBatches} batches)`, 'info');

    for (let i = 0; i < users.length && !shouldStop.current; i += batchSize) {
      const batch = users.slice(i, i + batchSize);
      appendLog(`ðŸ”¨ Processing batch ${Math.floor(i/batchSize) + 1}/${totalBatches}`, 'info');

      const batchResults = await processBatch(batch);
      successCount += batchResults.filter(Boolean).length;
      errorCount += batchResults.filter(x => !x).length;

      setProgress(Math.round(((i + batchSize) / users.length) * 100));

      if (shouldStop.current) break;
    }

    appendLog(
      `ðŸŽ‰ Process ${shouldStop.current ? 'stopped' : 'completed'}. Success: ${successCount}, Errors: ${errorCount}`,
      errorCount > 0 ? 'warning' : 'success'
    );

    setProcessing(false);
  };

  const stopProcessing = () => {
    shouldStop.current = true;
    appendLog("â¹ Stopping process after current batch completes...", 'warning');
  };

  // Export functions
  const exportUsersToCSV = async () => {
    appendLog("ðŸ“¦ Starting DHIS2 user export...", 'info');
    setProcessing(true);

    try {
      let seenIds = new Set();
      let usersList = [];
      let nextUrl = `${BASE_URL}/users.json?fields=id,name,username,userGroups[name],userRoles[name],lastLogin,organisationUnits[ancestors[name],name,id]&paging=true&pageSize=1000`;

      while (nextUrl) {
        const response = await apiRequest({ method: 'get', url: nextUrl });
        if (response.status !== 200) break;

        const data = response.data;
        const users = data.users || [];
        const pager = data.pager || {};

        for (const user of users) {
          const uid = user.id;
          if (!uid || seenIds.has(uid)) continue;
          seenIds.add(uid);

          const userGroups = user.userGroups?.map(g => g.name).join("; ") || "";
          const userRoles = user.userRoles?.map(r => r.name).join("; ") || "";
          const lastLogin = user.lastLogin || "";

          let schoolPaths = [];
          let schoolUIDs = [];

          (user.organisationUnits || []).forEach(ou => {
            if (ou.ancestors?.length === 4) {
              const path = [...ou.ancestors.map(a => a.name), ou.name].join(" > ");
              schoolPaths.push(path);
              schoolUIDs.push(ou.id || "");
            }
          });

          usersList.push({
            id: uid,
            name: user.name || '',
            username: user.username || '',
            userGroups,
            userRoles,
            lastLogin,
            schoolPath: schoolPaths.join(" | "),
            schoolUID: schoolUIDs.join(" | ")
          });
        }

        appendLog(`âœ… Page ${pager.page} fetched. Total: ${seenIds.size}`, 'success');
        nextUrl = pager.nextPage || null;
      }

      setExportedUsers(usersList);
      appendLog(`ðŸŽ‰ Export complete. Total users: ${seenIds.size}`, 'success');
    } catch (error) {
      appendLog(`âŒ Export failed: ${error.message}`, 'error');
    } finally {
      setProcessing(false);
    }
  };

  const exportFilteredUsers = (format = 'csv') => {
    const filtered = exportedUsers.filter(u =>
      u.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
      u.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
    );

    if (filtered.length === 0) {
      appendLog('âš ï¸ No users match your filters', 'warning');
      return;
    }

    try {
      let content, mimeType, extension;

      if (format === 'csv') {
        const headers = availableColumns
          .filter(col => selectedColumns.includes(col.id))
          .map(col => col.label);

        const rows = filtered.map(user => 
          selectedColumns.map(col => {
            const value = user[col] || '';
            return value.includes(',') ? `"${value}"` : value;
          }).join(',')
        );

        content = [headers.join(','), ...rows].join('\n');
        mimeType = 'text/csv;charset=utf-8;';
        extension = 'csv';
      } else {
        content = JSON.stringify(filtered, null, 2);
        mimeType = 'application/json';
        extension = 'json';
      }

      const blob = new Blob([content], { type: mimeType });
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.setAttribute('download', `dhis2_users_${new Date().toISOString().slice(0,10)}.${extension}`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      appendLog(`âœ… Exported ${filtered.length} users as ${format.toUpperCase()}`, 'success');
    } catch (error) {
      appendLog(`âŒ Export error: ${error.message}`, 'error');
    }
  };

  // UI helpers
  const toggleTheme = () => setTheme(theme === 'light' ? 'dark' : 'light');
  const filteredUsers = exportedUsers.filter(user =>
    user.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
    user.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
  );
  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);

  const handleNextPage = () => currentPage < totalPages && setCurrentPage(currentPage + 1);
  const handlePrevPage = () => currentPage > 1 && setCurrentPage(currentPage - 1);

  const toggleColumn = (columnId) => {
    setSelectedColumns(prev => 
      prev.includes(columnId) ? prev.filter(id => id !== columnId) : [...prev, columnId]
    );
  };

  const getLogColor = (type) => {
    switch (type) {
      case 'error': return 'text-red-400';
      case 'warning': return 'text-yellow-400';
      case 'success': return 'text-green-400';
      default: return 'text-gray-300';
    }
  };

  return (
    <div className={`min-h-screen p-4 ${theme === 'dark' ? 'dark bg-gray-900' : 'bg-gray-50'}`}>
      <div className="max-w-7xl mx-auto rounded-2xl shadow-lg overflow-hidden">
        {/* Header */}
        <div className={`p-6 ${theme === 'dark' ? 'bg-gray-800' : 'bg-blue-600'} text-white`}>
          <div className="flex justify-between items-center">
            <div className="flex items-center space-x-3">
              <FiUsers className="text-2xl" />
              <h1 className="text-2xl font-bold">DHIS2 User Manager</h1>
            </div>
            <div className="flex space-x-4">
              <button 
                onClick={toggleTheme} 
                className="flex items-center space-x-1 bg-black bg-opacity-20 px-3 py-1 rounded hover:bg-opacity-30 transition"
              >
                {theme === 'light' ? <FiMoon /> : <FiSun />}
                <span>{theme === 'light' ? 'Dark' : 'Light'} Mode</span>
              </button>
              <button className="flex items-center space-x-1 bg-black bg-opacity-20 px-3 py-1 rounded hover:bg-opacity-30 transition">
                <FiHelpCircle />
                <span>Help</span>
              </button>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className={`p-6 ${theme === 'dark' ? 'bg-gray-700' : 'bg-white'}`}>
          {/* Connection Status */}
          <div className="mb-4 p-2 rounded-md bg-blue-50 border border-blue-200 dark:bg-gray-800 dark:border-gray-700">
            <div className="flex items-center">
              <span className="font-medium mr-2">Server Status:</span>
              <span className={`inline-block w-3 h-3 rounded-full mr-2 ${
                connectionStatus === 'connected' ? 'bg-green-500' : 'bg-red-500'
              }`}></span>
              <span>{connectionStatus}</span>
            </div>
            {connectionStatus === 'disconnected' && (
              <p className="mt-1 text-sm text-red-600 dark:text-red-400">
                Connection issues detected. Some operations may fail.
              </p>
            )}
          </div>

          {/* Tabs */}
          <div className="flex border-b mb-6">
            <button
              className={`px-4 py-2 font-medium flex items-center ${
                activeTab === 'import' 
                  ? theme === 'dark' 
                    ? 'text-blue-300 border-b-2 border-blue-300' 
                    : 'text-blue-600 border-b-2 border-blue-600' 
                  : theme === 'dark' 
                    ? 'text-gray-400' 
                    : 'text-gray-600'
              }`}
              onClick={() => setActiveTab('import')}
            >
              <FiUpload className="mr-2" />
              Import Users
            </button>
            <button
              className={`px-4 py-2 font-medium flex items-center ${
                activeTab === 'export' 
                  ? theme === 'dark' 
                    ? 'text-blue-300 border-b-2 border-blue-300' 
                    : 'text-blue-600 border-b-2 border-blue-600' 
                  : theme === 'dark' 
                    ? 'text-gray-400' 
                    : 'text-gray-600'
              }`}
              onClick={() => setActiveTab('export')}
            >
              <FiDownload className="mr-2" />
              Export Users
            </button>
          </div>

          {/* Import Tab */}
          {activeTab === 'import' && (
            <div className="space-y-6">
              <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                <h2 className="text-lg font-semibold mb-3">Import Users</h2>
                <div className="flex flex-wrap gap-4">
                  <div className="flex-1 min-w-[300px]">
                    <label className="block mb-2 font-medium">Upload File</label>
                    <div className="flex items-center space-x-2">
                      <label className={`flex-1 border rounded-lg p-2 cursor-pointer flex items-center ${
                        theme === 'dark' ? 'border-gray-600 bg-gray-900 hover:bg-gray-800' : 'border-gray-300 bg-white hover:bg-gray-50'
                      } transition`}>
                        <FiUpload className="mr-2" />
                        <span>Choose File</span>
                        <input 
                          type="file" 
                          accept=".json,.csv" 
                          onChange={handleFileUpload} 
                          disabled={processing}
                          className="hidden" 
                        />
                      </label>
                      <div className="flex space-x-2">
                        <button 
                          onClick={downloadSampleJson}
                          className={`px-4 py-2 rounded-lg flex items-center ${
                            theme === 'dark' ? 'bg-purple-700 hover:bg-purple-600' : 'bg-purple-600 hover:bg-purple-500'
                          } text-white transition`}
                          title="Download JSON sample"
                        >
                          <FiDownload className="mr-2" />
                          JSON
                        </button>
                        <button 
                          onClick={downloadSampleCSV}
                          className={`px-4 py-2 rounded-lg flex items-center ${
                            theme === 'dark' ? 'bg-blue-700 hover:bg-blue-600' : 'bg-blue-600 hover:bg-blue-500'
                          } text-white transition`}
                          title="Download CSV template"
                        >
                          <FiDownload className="mr-2" />
                          CSV
                        </button>
                      </div>
                    </div>
                    {users.length > 0 && (
                      <p className="mt-2 text-sm text-green-500">
                        âœ… {users.length} users loaded
                      </p>
                    )}
                  </div>

                  <div className="flex-1 min-w-[300px]">
                    <label className="block mb-2 font-medium">Batch Settings</label>
                    <select
                      value={batchSize}
                      onChange={(e) => setBatchSize(Number(e.target.value))}
                      className={`w-full border rounded-lg p-2 ${
                        theme === 'dark' ? 'bg-gray-900 border-gray-600' : 'bg-white border-gray-300'
                      }`}
                    >
                      {[1, 2, 5, 10].map(size => (
                        <option key={size} value={size}>{size} users/batch</option>
                      ))}
                    </select>
                  </div>
                </div>
              </div>

              <div className="flex flex-wrap gap-4">
                <button 
                  onClick={processUsers} 
                  disabled={processing || users.length === 0 || connectionStatus !== 'connected'}
                  className={`px-4 py-2 rounded-lg flex items-center ${
                    processing || users.length === 0 || connectionStatus !== 'connected'
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-green-600 hover:bg-green-500'
                  } text-white transition`}
                >
                  <FiPlay className="mr-2" />
                  Start Import
                </button>
                <button 
                  onClick={stopProcessing} 
                  disabled={!processing}
                  className={`px-4 py-2 rounded-lg flex items-center ${
                    !processing 
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-red-600 hover:bg-red-500'
                  } text-white transition`}
                >
                  <FiSquare className="mr-2" />
                  Stop
                </button>
              </div>

              {processing && (
                <div className="w-full bg-gray-200 rounded-full h-2.5 overflow-hidden dark:bg-gray-700">
                  <div 
                    className="bg-blue-600 h-2.5 transition-all duration-300 dark:bg-blue-400" 
                    style={{ width: `${progress}%` }} 
                  />
                </div>
              )}

              {/* Activity Log */}
              <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                <h3 className="font-medium mb-2">Activity Log</h3>
                <div className={`h-60 overflow-y-auto p-3 rounded ${
                  theme === 'dark' ? 'bg-black' : 'bg-gray-200'
                }`}>
                  {log.length === 0 ? (
                    <p className={`text-center ${
                      theme === 'dark' ? 'text-gray-500' : 'text-gray-600'
                    }`}>
                      No activity yet
                    </p>
                  ) : (
                    <div className="font-mono text-sm space-y-1">
                      {log.map((entry, i) => (
                        <div key={i} className={getLogColor(entry.type)}>
                          <span className="opacity-70 mr-2">[{entry.timestamp}]</span>
                          {entry.message}
                        </div>
                      ))}
                      <div ref={logEndRef} />
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Export Tab */}
          {activeTab === 'export' && (
            <div className="space-y-6">
              <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                <h2 className="text-lg font-semibold mb-3">Export Users</h2>
                <button 
                  onClick={exportUsersToCSV} 
                  disabled={processing}
                  className={`px-4 py-2 rounded-lg flex items-center ${
                    processing 
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-blue-600 hover:bg-blue-500'
                  } text-white transition`}
                >
                  <FiDownload className="mr-2" />
                  Fetch Users from DHIS2
                </button>
              </div>

              {exportedUsers.length > 0 && (
                <>
                  <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                    <h3 className="font-medium mb-3">Filter & Export</h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                      <div>
                        <label className="block mb-1">Username</label>
                        <input
                          type="text"
                          placeholder="Filter by username"
                          value={usernameFilter}
                          onChange={(e) => setUsernameFilter(e.target.value)}
                          className={`w-full p-2 rounded border ${
                            theme === 'dark' ? 'bg-gray-900 border-gray-600' : 'bg-white border-gray-300'
                          }`}
                        />
                      </div>
                      <div>
                        <label className="block mb-1">Organization Unit</label>
                        <input
                          type="text"
                          placeholder="Filter by org unit path"
                          value={orgUnitFilter}
                          onChange={(e) => setOrgUnitFilter(e.target.value)}
                          className={`w-full p-2 rounded border ${
                            theme === 'dark' ? 'bg-gray-900 border-gray-600' : 'bg-white border-gray-300'
                          }`}
                        />
                      </div>
                    </div>

                    <div className="mb-4">
                      <label className="block mb-2">Columns to Export</label>
                      <div className="flex flex-wrap gap-2">
                        {availableColumns.map(col => (
                          <label key={col.id} className="flex items-center space-x-1">
                            <input
                              type="checkbox"
                              checked={selectedColumns.includes(col.id)}
                              onChange={() => toggleColumn(col.id)}
                              className="rounded"
                            />
                            <span>{col.label}</span>
                          </label>
                        ))}
                      </div>
                    </div>

                    <div className="flex flex-wrap gap-3">
                      <button 
                        onClick={() => exportFilteredUsers('csv')}
                        className={`px-4 py-2 rounded-lg flex items-center ${
                          theme === 'dark' ? 'bg-green-700 hover:bg-green-600' : 'bg-green-600 hover:bg-green-500'
                        } text-white transition`}
                      >
                        <FiDownload className="mr-2" />
                        Export as CSV
                      </button>
                      <button 
                        onClick={() => exportFilteredUsers('json')}
                        className={`px-4 py-2 rounded-lg flex items-center ${
                          theme === 'dark' ? 'bg-yellow-700 hover:bg-yellow-600' : 'bg-yellow-600 hover:bg-yellow-500'
                        } text-white transition`}
                      >
                        <FiDownload className="mr-2" />
                        Export as JSON
                      </button>
                    </div>
                  </div>

                  <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                    <h3 className="font-medium mb-3">User Data ({filteredUsers.length} users)</h3>
                    <div className="overflow-x-auto">
                      <table className="min-w-full border-collapse">
                        <thead>
                          <tr className={`${theme === 'dark' ? 'bg-gray-900' : 'bg-gray-200'}`}>
                            {selectedColumns.includes('id') && <th className="p-2 border text-left">ID</th>}
                            {selectedColumns.includes('name') && <th className="p-2 border text-left">Name</th>}
                            {selectedColumns.includes('username') && <th className="p-2 border text-left">Username</th>}
                            {selectedColumns.includes('userGroups') && <th className="p-2 border text-left">Groups</th>}
                            {selectedColumns.includes('userRoles') && <th className="p-2 border text-left">Roles</th>}
                            {selectedColumns.includes('lastLogin') && <th className="p-2 border text-left">Last Login</th>}
                            {selectedColumns.includes('schoolPath') && <th className="p-2 border text-left">School Path</th>}
                            {selectedColumns.includes('schoolUID') && <th className="p-2 border text-left">School UID</th>}
                          </tr>
                        </thead>
                        <tbody>
                          {currentUsers.map((user, idx) => (
                            <tr 
                              key={idx} 
                              className={`${
                                idx % 2 === 0 
                                  ? theme === 'dark' 
                                    ? 'bg-gray-700' 
                                    : 'bg-white' 
                                  : theme === 'dark' 
                                    ? 'bg-gray-800' 
                                    : 'bg-gray-50'
                              }`}
                            >
                              {selectedColumns.includes('id') && <td className="p-2 border text-xs">{user.id}</td>}
                              {selectedColumns.includes('name') && <td className="p-2 border text-xs">{user.name}</td>}
                              {selectedColumns.includes('username') && <td className="p-2 border text-xs">{user.username}</td>}
                              {selectedColumns.includes('userGroups') && <td className="p-2 border text-xs">{user.userGroups}</td>}
                              {selectedColumns.includes('userRoles') && <td className="p-2 border text-xs">{user.userRoles}</td>}
                              {selectedColumns.includes('lastLogin') && <td className="p-2 border text-xs">{user.lastLogin}</td>}
                              {selectedColumns.includes('schoolPath') && <td className="p-2 border text-xs">{user.schoolPath}</td>}
                              {selectedColumns.includes('schoolUID') && <td className="p-2 border text-xs">{user.schoolUID}</td>}
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>

                    {totalPages > 1 && (
                      <div className="mt-4 flex justify-between items-center">
                        <button 
                          onClick={handlePrevPage} 
                          disabled={currentPage === 1}
                          className={`px-4 py-2 rounded ${
                            currentPage === 1 
                              ? 'opacity-50 cursor-not-allowed' 
                              : theme === 'dark' 
                                ? 'bg-gray-600 hover:bg-gray-500' 
                                : 'bg-gray-200 hover:bg-gray-300'
                          } transition`}
                        >
                          Previous
                        </button>
                        <span>
                          Page {currentPage} of {totalPages}
                        </span>
                        <button 
                          onClick={handleNextPage} 
                          disabled={currentPage === totalPages}
                          className={`px-4 py-2 rounded ${
                            currentPage === totalPages 
                              ? 'opacity-50 cursor-not-allowed' 
                              : theme === 'dark' 
                                ? 'bg-gray-600 hover:bg-gray-500' 
                                : 'bg-gray-200 hover:bg-gray-300'
                          } transition`}
                        >
                          Next
                        </button>
                      </div>
                    )}
                  </div>
                </>
              )}

              {/* Activity Log */}
              <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-100'}`}>
                <h3 className="font-medium mb-2">Activity Log</h3>
                <div className={`h-60 overflow-y-auto p-3 rounded ${
                  theme === 'dark' ? 'bg-black' : 'bg-gray-200'
                }`}>
                  {log.length === 0 ? (
                    <p className={`text-center ${
                      theme === 'dark' ? 'text-gray-500' : 'text-gray-600'
                    }`}>
                      No activity yet
                    </p>
                  ) : (
                    <div className="font-mono text-sm space-y-1">
                      {log.map((entry, i) => (
                        <div key={i} className={getLogColor(entry.type)}>
                          <span className="opacity-70 mr-2">[{entry.timestamp}]</span>
                          {entry.message}
                        </div>
                      ))}
                      <div ref={logEndRef} />
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
