// src/App.js
import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const BASE_URL = "https://emis.dhis2nigeria.org.ng/dhis/api";
const HEADERS = {
  'Content-Type': 'application/json',
  'Authorization': 'ApiToken d2pat_DAULytLK7GUBSlfknZ6ffVxIfifC8T9c3885349470'
};

function App() {
  const [users, setUsers] = useState([]);
  const [log, setLog] = useState([]);
  const [processing, setProcessing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [exportedUsers, setExportedUsers] = useState(() => {
    const cached = sessionStorage.getItem('exportedUsers');
    return cached ? JSON.parse(cached) : [];
  });
  const [usernameFilter, setUsernameFilter] = useState('');
  const [orgUnitFilter, setOrgUnitFilter] = useState('');
  const [theme, setTheme] = useState('light');
  const [currentPage, setCurrentPage] = useState(1);
  const usersPerPage = 10;
  const shouldStop = useRef(false);

  useEffect(() => {
    const meta = {
    lastExportTime: new Date().toISOString(),
    total: exportedUsers.length
  };
  sessionStorage.setItem('exportedUsersMeta', JSON.stringify(meta));
  }, [exportedUsers]);

  const appendLog = (message) => {
    setLog(log => [...log, message]);
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onload = event => {
      const data = JSON.parse(event.target.result);
      setUsers(data);
      appendLog(`âœ… Loaded ${data.length} users`);
    };

    reader.readAsText(file);
  };

  const getUserId = async (username) => {
    try {
      const res = await axios.get(`${BASE_URL}/users?filter=username:eq:${username}&fields=id`, { headers: HEADERS });
      return res.data.users?.[0]?.id || null;
    } catch {
      return null;
    }
  };

  const updateUser = async (user, id) => {
    const copy = { ...user };
    delete copy.password;
    await axios.put(`${BASE_URL}/users/${id}`, copy, { headers: HEADERS });
    appendLog(`ðŸ”„ Updated user: ${user.username}`);
  };

  const createUser = async (user) => {
    try {
      await axios.post(`${BASE_URL}/users`, user, { headers: HEADERS });
      appendLog(`ðŸ†• Created user: ${user.username}`);
    } catch (error) {
      if (error.response?.status === 409) {
        const id = await getUserId(user.username);
        if (id) {
          appendLog(`âš ï¸ Conflict: Updating user ${user.username}`);
          await updateUser(user, id);
        } else {
          appendLog(`âŒ Conflict and ID not found: ${user.username}`);
        }
      } else {
        appendLog(`âŒ Error for ${user.username}: ${error.message}`);
      }
    }
  };

  const processUsers = async () => {
    setProcessing(true);
    shouldStop.current = false;
    setProgress(0);
    setLog([]);

    for (let i = 0; i < users.length; i++) {
      if (shouldStop.current) {
        appendLog("ðŸ›‘ Process stopped by user.");
        break;
      }

      const user = users[i];
      const id = await getUserId(user.username);

      if (id) {
        await updateUser(user, id);
      } else {
        await createUser(user);
      }

      setProgress(Math.round(((i + 1) / users.length) * 100));
    }

    setProcessing(false);
  };

  const stopProcessing = () => {
    shouldStop.current = true;
  };

  const exportUsersToCSV = async () => {
    appendLog("ðŸ“¦ Starting DHIS2 user export...");

    const fieldnames = ["id", "name", "username", "userGroups", "userRoles", "lastLogin", "schoolPath", "schoolUID"];
    let seenIds = new Set();
    let usersCSV = [fieldnames.join(",")];
    let usersList = [];
    let nextUrl = `${BASE_URL}/users.json?fields=id,name,username,userGroups[name],userRoles[name],lastLogin,organisationUnits[ancestors[name],name,id]&paging=true&pageSize=1000`;

    while (nextUrl) {
      try {
        const response = await axios.get(nextUrl, { headers: HEADERS });
        if (response.status !== 200) break;

        const data = response.data;
        const users = data.users || [];
        const pager = data.pager || {};

        for (const user of users) {
          const uid = user.id;
          if (!uid || seenIds.has(uid)) continue;
          seenIds.add(uid);

          const userGroups = user.userGroups?.map(g => g.name).join("; ") || "";
          const userRoles = user.userRoles?.map(r => r.name).join("; ") || "";
          const lastLogin = user.lastLogin || "";

          let schoolPaths = [];
          let schoolUIDs = [];

          (user.organisationUnits || []).forEach(ou => {
            if (ou.ancestors?.length === 4) {
              const path = [...ou.ancestors.map(a => a.name), ou.name].join(" > ");
              schoolPaths.push(path);
              schoolUIDs.push(ou.id || "");
            }
          });

          const row = [
            uid,
            `"${user.name || ""}"`,
            user.username || "",
            `"${userGroups}"`,
            `"${userRoles}"`,
            lastLogin,
            `"${schoolPaths.join(" | ")}"`,
            `"${schoolUIDs.join(" | ")}"`
          ].join(",");

          usersCSV.push(row);

          usersList.push({
            id: uid,
            name: user.name || '',
            username: user.username || '',
            userGroups,
            userRoles,
            lastLogin,
            schoolPath: schoolPaths.join(" | "),
            schoolUID: schoolUIDs.join(" | ")
          });
        }

        appendLog(`âœ… Page ${pager.page} fetched. Total: ${seenIds.size}`);
        nextUrl = pager.nextPage || null;

      } catch (err) {
        appendLog(`âŒ Error fetching users: ${err.message}`);
        break;
      }
    }

    const blob = new Blob([usersCSV.join("\n")], { type: "text/csv;charset=utf-8;" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.setAttribute("download", `dhis2_users_export_${new Date().toISOString().slice(0,10)}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    setExportedUsers(usersList);
    appendLog(`ðŸŽ‰ Export complete. Total users: ${seenIds.size}`);
    setTimeout(() => setLog([]), 5000);
  };

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  const filteredUsers = exportedUsers.filter(user =>
    user.username.toLowerCase().includes(usernameFilter.toLowerCase()) &&
    user.schoolPath.toLowerCase().includes(orgUnitFilter.toLowerCase())
  );

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);

  const handleNextPage = () => {
    if (currentPage < totalPages) setCurrentPage(currentPage + 1);
  };

  const handlePrevPage = () => {
    if (currentPage > 1) setCurrentPage(currentPage - 1);
  };

  return (
    <div className={`${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-100 text-black'} min-h-screen p-6`}>
      <div className="max-w-6xl mx-auto rounded-2xl shadow p-6 bg-white dark:bg-gray-800">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-2xl font-bold text-blue-700 dark:text-blue-300">DHIS2 User Manager</h1>
          <button onClick={toggleTheme} className="text-sm text-gray-600 dark:text-gray-300 hover:underline">
            Toggle {theme === 'light' ? 'Dark' : 'Light'} Mode
          </button>
        </div>

        <div className="space-y-4">
          <input
            type="file"
            accept=".json"
            onChange={handleFileUpload}
            disabled={processing}
            className="w-full border border-gray-300 rounded p-2"
          />

          <div className="flex flex-wrap gap-4">
            <button onClick={processUsers} disabled={processing || users.length === 0} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
              Start Import
            </button>
            <button onClick={stopProcessing} disabled={!processing} className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600">
              Stop
            </button>
            <button onClick={exportUsersToCSV} disabled={processing} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
              Export Users to CSV
            </button>
          </div>

          <div className="w-full bg-gray-200 rounded h-4 overflow-hidden">
            <div className="bg-blue-500 h-full transition-all duration-300" style={{ width: `${progress}%` }} />
          </div>

          <div className="bg-black text-white p-4 rounded h-60 overflow-auto text-sm font-mono whitespace-pre-wrap">
            {log.join("\n")}
          </div>

          {exportedUsers.length > 0 && (
            <>
              <div className="flex flex-wrap gap-4 items-center">
                <input
                  type="text"
                  placeholder="Filter by Username"
                  value={usernameFilter}
                  onChange={(e) => setUsernameFilter(e.target.value)}
                  className="p-2 border rounded w-64"
                />
                <input
                  type="text"
                  placeholder="Filter by Org Unit"
                  value={orgUnitFilter}
                  onChange={(e) => setOrgUnitFilter(e.target.value)}
                  className="p-2 border rounded w-64"
                />
              </div>

              <table className="min-w-full mt-4 border border-gray-300">
                <thead>
                  <tr className="bg-gray-100 dark:bg-gray-700">
                    <th className="border p-2">ID</th>
                    <th className="border p-2">Name</th>
                    <th className="border p-2">Username</th>
                    <th className="border p-2">Groups</th>
                    <th className="border p-2">Roles</th>
                    <th className="border p-2">Last Login</th>
                    <th className="border p-2">School Path</th>
                  </tr>
                </thead>
                <tbody>
                  {currentUsers.map((user, idx) => (
                    <tr key={idx} className="even:bg-gray-50 dark:even:bg-gray-800">
                      <td className="border p-2 text-xs">{user.id}</td>
                      <td className="border p-2 text-xs">{user.name}</td>
                      <td className="border p-2 text-xs">{user.username}</td>
                      <td className="border p-2 text-xs">{user.userGroups}</td>
                      <td className="border p-2 text-xs">{user.userRoles}</td>
                      <td className="border p-2 text-xs">{user.lastLogin}</td>
                      <td className="border p-2 text-xs">{user.schoolPath}</td>
                    </tr>
                  ))}
                </tbody>
              </table>

              <div className="mt-4 flex justify-between items-center">
                <button onClick={handlePrevPage} disabled={currentPage === 1} className="px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded disabled:opacity-50">Previous</button>
                <span>Page {currentPage} of {totalPages}</span>
                <button onClick={handleNextPage} disabled={currentPage === totalPages} className="px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded disabled:opacity-50">Next</button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
